import { Suspense } from 'react';
import { fetchQuizByIdOptimized } from '../../lib/supabaseQuizServiceOptimized';
import { QuizDataProvider } from '../../components/QuizDataProvider';

// Set revalidation period for this page
export const revalidate = 3600; // Revalidate page every hour

// Add dynamic = 'force-dynamic' if you want to ensure this is always server-rendered
// export const dynamic = 'force-dynamic';

// Server Component for Quiz Rendering
export default async function CachedQuizPage({ params }: { params: { quizId: string } }) {
  const { quizId } = params;

  // Pre-fetch quiz data on the server (will be cached according to revalidate setting)
  const quizData = await fetchQuizByIdOptimized(quizId);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Quiz: {quizId}</h1>

      <Suspense fallback={<div>Loading quiz details...</div>}>
        {quizData ? (
          <QuizWithData initialData={quizData} quizId={quizId} />
        ) : (
          <div className="text-red-500">Quiz not found or error loading quiz data.</div>
        )}
      </Suspense>
    </div>
  );
}

// Client Component that receives the server-fetched data 
// and can also refresh it client-side if needed
function QuizWithData({ initialData, quizId }: { initialData: any; quizId: string }) {
  return (
    <QuizDataProvider quizId={quizId} initialData={initialData}>
      <QuizDisplay />
    </QuizDataProvider>
  );
}

// Client Component that displays the quiz using the context
function QuizDisplay() {
  // This would normally use the useQuizData() hook to get quiz data from context

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4">Quiz Summary</h2>

      <div className="space-y-4">
        <p>
          This page demonstrates the cached quiz data fetching implementation.
          The quiz data is pre-fetched on the server and then passed to the client,
          with a revalidation period of 1 hour.
        </p>

        <div className="bg-gray-100 p-4 rounded">
          <h3 className="font-medium">Benefits of this implementation:</h3>
          <ul className="list-disc ml-5 mt-2">
            <li>Server-side rendering with data pre-fetching</li>
            <li>Automatic cache revalidation after 1 hour</li>
            <li>Optimized batch fetching for related quiz data</li>
            <li>In-memory caching for subsequent requests</li>
            <li>Consistent data between server and client</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export type QuestionType =
  | 'drag_and_drop'
  | 'dropdown_selection'
  | 'multi'
  | 'single_selection'
  | 'order'
  | 'yes_no'
  | 'yesno_multi';

export type Difficulty = 'easy' | 'medium' | 'hard';

export interface BaseQuestion {
  id: string; // UUID
  type: QuestionType;
  question: string;
  points: number;
  quiz_tag: string; // Corresponds to Quiz.id
  difficulty: Difficulty;
  explanation?: string | null;
  feedback_correct: string;
  feedback_incorrect: string;
  created_at: string; // TIMESTAMPTZ
  updated_at: string; // TIMESTAMPTZ
}

export interface SelectionOption {
  option_id: string;
  text: string;
  // Note: The database table `single_selection_options` does not have `question_id` directly in its TS type,
  // as options will be nested under a question. The `question_id` is for the DB relation.
}

// Updated SingleSelectionQuestion to include specific properties
export interface SingleSelectionQuestion extends BaseQuestion {
  type: 'single_selection';
  options: SelectionOption[];
  // The `single_selection_correct_answer` table stores `option_id` for the correct answer,
  // linked to the `question_id`.
  correctAnswerOptionId: string; 
}

// Multi-choice question where users can select multiple answers
export interface MultiChoiceQuestion extends BaseQuestion {
  type: 'multi';
  options: SelectionOption[];
  // An array of correct option IDs
  correctAnswerOptionIds: string[];
}

export interface DragAndDropTarget {
  target_id: string;
  text: string;
}

export interface DragAndDropOption {
  option_id: string;
  text: string;
}

export interface DragAndDropCorrectPair {
  option_id: string;
  target_id: string;
}

// Drag and drop question where users match options to targets
export interface DragAndDropQuestion extends BaseQuestion {
  type: 'drag_and_drop';
  targets: DragAndDropTarget[];
  options: DragAndDropOption[];
  correctPairs: DragAndDropCorrectPair[];
}

// New types for OrderQuestion
export interface OrderItem {
  item_id: string;
  text: string;
}

// Order question where users arrange items in correct sequence
export interface OrderQuestion extends BaseQuestion {
  type: 'order';
  items: OrderItem[];
  correctOrder: string[]; // Array of item_ids in correct order
}

// Type for order question answers - maps slot names to item IDs
// e.g. { "slot_0": "item1", "slot_1": "item2", "slot_2": null }
export type OrderQuestionAnswer = Record<string, string | null>;

// New types for DropdownSelectionQuestion
export interface DropdownOption {
  option_id: string;
  text: string;
  // is_correct is part of the dropdown_selection_options table schema
  // but might not be directly exposed to the client in DropdownSelectionQuestion if not needed for rendering all options.
  // However, the migration script keeps it, and it can be useful for client-side hints or direct display if design changes.
  is_correct?: boolean; 
}

export interface DropdownPlaceholderTarget {
  key: string; // The placeholder text, e.g., "option_set1"
  correctOptionText: string; // The text of the correct option for this placeholder
}

export interface DropdownSelectionQuestion extends BaseQuestion {
  type: 'dropdown_selection';
  options: DropdownOption[]; // All available options for any dropdown in this question
  placeholderTargets: Record<string, DropdownPlaceholderTarget>; // Maps placeholder key to its correct target details
}

// Yes/No question where user selects a single yes or no answer
export interface YesNoQuestion extends BaseQuestion {
  type: 'yes_no';
  correctAnswer: boolean; // true for 'yes', false for 'no'
}

// Multi-statement Yes/No question where user answers yes or no to each statement
export interface YesNoMultiQuestion extends BaseQuestion {
  type: 'yesno_multi';
  statements: YesNoStatement[];
  correctAnswers: boolean[]; // Array of boolean values (true for 'yes', false for 'no')
}

export interface YesNoStatement {
  statement_id: string;
  text: string;
}

// AnyQuestion will be a union of all specific question types
export type AnyQuestion = 
  | SingleSelectionQuestion 
  | MultiChoiceQuestion 
  | DragAndDropQuestion
  | DropdownSelectionQuestion
  | OrderQuestion // Added OrderQuestion
  | YesNoQuestion
  | YesNoMultiQuestion;

export interface Quiz {
  id: string; // e.g. azure-a102
  title: string;
  description?: string | null;
  quiz_type?: string | null;
  settings?: Record<string, any> | null; // JSONB
  author?: string | null;
  difficulty: Difficulty;
  quiz_topic: string; // Usually same as id
  created_at: string; // TIMESTAMPTZ
  updated_at: string; // TIMESTAMPTZ
  questions: AnyQuestion[];
}

