/**
 * Performance optimization toolkit for the quiz application
 * 
 * This module centralizes all performance optimization utilities and hooks:
 * 
 * Rendering Optimizations:
 * - MemoWithName: Enhanced React.memo with better debug names
 * - createPropsComparator: Custom prop comparison for React.memo
 * - withRenderLog: HOC to track component renders during development
 * 
 * CSS Optimizations:
 * - tw: Utility for conditionally applying Tailwind classes
 * - twIf: Apply different classes based on a condition
 * - twMerge: Merge Tailwind classes resolving conflicts
 * 
 * Data Loading Optimizations:
 * - useDataCache: Smart data caching hook
 * - DataCache: In-memory cache with expiration
 * - useNetwork: Network quality and connection detection
 * 
 * Performance Measurement:
 * - useRenderMetrics: Track component render performance
 * - usePerformanceMetrics: More detailed performance metrics
 * 
 * Input Handling:
 * - useDebounce: Debounce rapidly changing values
 * - useDebouncedCallback: Debounce function calls
 * - useThrottle: Throttle frequent function calls
 * 
 * Large Lists:
 * - useVirtualizedList: Only render visible items in long lists
 * 
 * Font Loading:
 * - useFontLoading: Control font loading behavior
 */

// Helper functions

/**
 * Check if the code is running on client or server
 */
export const isClient = typeof window !== 'undefined';
export const isServer = !isClient;

/**
 * Check if the app is running in development mode
 */
export const isDev = process.env.NODE_ENV === 'development';
export const isProd = process.env.NODE_ENV === 'production';

/**
 * RAF (requestAnimationFrame) helper for scheduling visual updates
 */
export function raf(callback: () => void): void {
  if (isClient) {
    requestAnimationFrame(callback);
  }
}

/**
 * Double RAF for ensuring DOM updates have been painted
 */
export function doubleRaf(callback: () => void): void {
  raf(() => {
    raf(callback);
  });
}

/**
 * Idle callback helper for non-critical work
 */
export function idle(callback: () => void, timeout = 1000): void {
  if (isClient) {
    if ('requestIdleCallback' in window) {
      window.requestIdleCallback(callback, { timeout });
    } else {
      setTimeout(callback, 100); // Fallback for browsers without requestIdleCallback
    }
  }
}

/**
 * Detect high-end device for enabling enhanced animations
 */
export function isHighEndDevice(): boolean {
  if (!isClient) return false;

  // Use hardware concurrency as a proxy for device capability
  const cores = navigator.hardwareConcurrency || 2;

  // Use deviceMemory API if available
  const memory = (navigator as any).deviceMemory || 4;

  return cores >= 4 && memory >= 4;
}

'use client';

import { useEffect, useState } from 'react';

/**
 * Font loading status
 */
type FontStatus = 'loading' | 'loaded' | 'error';

/**
 * Interface for the font loader options
 */
interface FontOptions {
  weight?: string | number;
  style?: 'normal' | 'italic' | 'oblique';
  display?: 'auto' | 'block' | 'swap' | 'fallback' | 'optional';
  preload?: boolean;
}

/**
 * Font cache to track which fonts are already loaded/loading
 */
const fontCache = new Map<string, FontStatus>();

/**
 * Create a unique key for a font configuration
 */
function getFontKey(family: string, options: FontOptions = {}): string {
  const { weight = 400, style = 'normal' } = options;
  return `${family}-${weight}-${style}`;
}

/**
 * Load a font using the CSS Font Loading API with fallback for older browsers
 */
export async function loadFont(
  family: string, 
  options: FontOptions = {}
): Promise<boolean> {
  const { 
    weight = 400, 
    style = 'normal', 
    display = 'swap'
  } = options;

  const fontKey = getFontKey(family, options);

  // Return immediately if already loaded
  if (fontCache.get(fontKey) === 'loaded') {
    return true;
  }

  // Skip if we're in SSR
  if (typeof document === 'undefined') {
    return false;
  }

  fontCache.set(fontKey, 'loading');

  try {
    // Try using Font Face Observer API if available
    if ('FontFace' in window) {
      const font = new FontFace(
        family, 
        `url(${encodeURIComponent(family)})`, 
        { weight: String(weight), style, display }
      );

      // Add to document fonts
      const loadedFace = await font.load();
      document.fonts.add(loadedFace);
      fontCache.set(fontKey, 'loaded');
      return true;
    }

    // Fallback method for older browsers
    return new Promise((resolve) => {
      const sampleText = 'BESbwy';

      // Create test elements
      const container = document.createElement('div');
      const fallbackSpan = document.createElement('span');
      const testSpan = document.createElement('span');

      Object.assign(container.style, {
        position: 'absolute',
        visibility: 'hidden',
        top: '-10000px',
        left: '-10000px',
        width: 'auto',
        height: 'auto',
        whiteSpace: 'nowrap'
      });

      // Set styles
      const baseStyle = {
        fontWeight: String(weight),
        fontStyle: style,
        fontSize: '100px',
        position: 'absolute'
      };

      Object.assign(fallbackSpan.style, {
        ...baseStyle,
        fontFamily: 'serif' // Fallback font
      });

      Object.assign(testSpan.style, {
        ...baseStyle,
        fontFamily: `"${family}", serif`
      });

      fallbackSpan.textContent = sampleText;
      testSpan.textContent = sampleText;

      container.appendChild(fallbackSpan);
      container.appendChild(testSpan);
      document.body.appendChild(container);

      // Compare widths after a timeout
      setTimeout(() => {
        const fallbackWidth = fallbackSpan.offsetWidth;
        const testWidth = testSpan.offsetWidth;

        const isLoaded = fallbackWidth !== testWidth;
        fontCache.set(fontKey, isLoaded ? 'loaded' : 'error');

        document.body.removeChild(container);
        resolve(isLoaded);
      }, 50);
    });
  } catch (err) {
    console.error(`Failed to load font ${family}:`, err);
    fontCache.set(fontKey, 'error');
    return false;
  }
}

/**
 * Preload a font
 */
export function preloadFont(
  family: string, 
  options: FontOptions = {}
): void {
  const { weight = 400, style = 'normal' } = options;

  // Skip if we're in SSR
  if (typeof document === 'undefined') return;

  const fontKey = getFontKey(family, options);

  // Skip if already loaded/loading
  if (fontCache.has(fontKey)) return;

  // Add preload link
  const preloadLink = document.createElement('link');
  preloadLink.href = `https://fonts.googleapis.com/css2?family=${encodeURIComponent(family)}:wght@${weight}&display=swap`;
  preloadLink.rel = 'preload';
  preloadLink.as = 'style';
  preloadLink.type = 'text/css';

  document.head.appendChild(preloadLink);

  // Also trigger actual loading
  loadFont(family, options).catch(console.error);
}

/**
 * React hook for font loading
 */
export function useFontLoading(
  family: string, 
  options: FontOptions = {}
): FontStatus {
  const fontKey = getFontKey(family, options);
  const [status, setStatus] = useState<FontStatus>(
    fontCache.get(fontKey) || 'loading'
  );

  useEffect(() => {
    // Skip if already loaded
    if (status === 'loaded') return;

    // Start loading the font
    loadFont(family, options)
      .then((success) => {
        setStatus(success ? 'loaded' : 'error');
      })
      .catch(() => {
        setStatus('error');
      });

  }, [family, options, status]);

  return status;
}

/**
 * Get font loading status
 */
export function getFontStatus(
  family: string, 
  options: FontOptions = {}
): FontStatus {
  const fontKey = getFontKey(family, options);
  return fontCache.get(fontKey) || 'loading';
}

'use client';

import React, { useEffect, useRef } from 'react';

/**
 * Performance metrics interface for tracking component performance
 */
interface PerformanceMetrics {
  componentName: string;
  renderCount: number;
  renderDuration: number;
  firstRender: boolean;
  renderStartTime: number;
  lastRenderTime: number;
  averageRenderDuration: number;
  totalRenderDuration: number;
}

// Store component metrics globally for easier access in dev tools
const componentMetrics: Record<string, PerformanceMetrics> = {};

// Define whether metrics are enabled (can be toggled via window.__ENABLE_METRICS__)
let metricsEnabled = typeof window !== 'undefined' && process.env.NODE_ENV === 'development';

// Configure metrics collection globally
if (typeof window !== 'undefined') {
  // @ts-ignore - Add global access for toggling metrics
  window.__ENABLE_METRICS__ = (enabled: boolean = true) => {
    metricsEnabled = enabled;
    console.log(`Performance metrics ${enabled ? 'enabled' : 'disabled'}`);
  };

  // @ts-ignore - Add global access to view metrics
  window.__GET_METRICS__ = () => {
    console.table(componentMetrics);
    return componentMetrics;
  };

  // @ts-ignore - Add global access to reset metrics
  window.__RESET_METRICS__ = () => {
    Object.keys(componentMetrics).forEach(key => delete componentMetrics[key]);
    console.log('Performance metrics reset');
  };
}

/**
 * Custom hook to track component rendering performance
 * 
 * @param componentName The name of the component to track
 * @param enabled Whether metrics collection is enabled for this component
 */
export function usePerformanceMetrics(componentName: string, enabled: boolean = true): PerformanceMetrics {
  const renderCountRef = useRef(0);
  const renderStartTimeRef = useRef(0);
  const metricsRef = useRef<PerformanceMetrics>({
    componentName,
    renderCount: 0,
    renderDuration: 0,
    firstRender: true,
    renderStartTime: 0,
    lastRenderTime: 0,
    averageRenderDuration: 0,
    totalRenderDuration: 0
  });

  // On mount, start tracking render time
  useEffect(() => {
    if (!metricsEnabled || !enabled) return;

    // End of render - calculate duration
    const renderEndTime = performance.now();
    const renderDuration = renderStartTimeRef.current > 0
      ? renderEndTime - renderStartTimeRef.current 
      : 0;

    // Update metrics
    const currentCount = renderCountRef.current;
    const metrics = metricsRef.current;

    metrics.renderCount = currentCount;
    metrics.renderDuration = renderDuration;
    metrics.lastRenderTime = renderEndTime;
    metrics.firstRender = currentCount === 1;
    metrics.totalRenderDuration += renderDuration;
    metrics.averageRenderDuration = metrics.totalRenderDuration / currentCount;

    // Store metrics globally
    componentMetrics[componentName] = metrics;

    // Log performance if significant (over 10ms)
    if (renderDuration > 10) {
      console.log(
        `%c${componentName} rendered in ${renderDuration.toFixed(2)}ms (${currentCount}${metrics.firstRender ? ', first render' : ''})`,
        'color: #ff9800; font-weight: bold;'
      );
    }
  });

  // Track render count and start time
  renderCountRef.current += 1;
  renderStartTimeRef.current = performance.now();

  return null;
}

/**
 * Performance wrapper component to track component rendering
 */
export function withPerformanceTracking<P extends object>(
  Component: React.ComponentType<P>,
  options: { componentName?: string; enabled?: boolean } = {}
): React.ComponentType<P> {
  const WrappedComponent = (props: P) => {
    const componentName = options.componentName || Component.displayName || Component.name || 'UnknownComponent';
    usePerformanceMetrics(componentName, options.enabled);
    return <Component {...props} />;
  };

  WrappedComponent.displayName = `WithPerformanceTracking(${Component.displayName || Component.name || 'Component'})`;

  return WrappedComponent;
}

'use client';

import React, { memo } from 'react';

/**
 * Enhanced React.memo with automatic naming for debugging
 * Preserves component displayName for better debugging in React DevTools
 * 
 * @param Component The component to memoize
 * @param propsAreEqual Optional comparison function
 * @returns Memoized component with preserved displayName
 */
export function MemoWithName<P extends object>(
  Component: React.ComponentType<P>,
  propsAreEqual?: (prevProps: Readonly<P>, nextProps: Readonly<P>) => boolean
): React.MemoExoticComponent<React.ComponentType<P>> {
  const displayName = Component.displayName || Component.name || 'Component';
  const MemoComponent = memo(Component, propsAreEqual);
  MemoComponent.displayName = `Memo(${displayName})`;
  return MemoComponent;
}

/**
 * Compare props shallowly but deeply compare specific keys
 * Use with React.memo for optimized comparisons
 * 
 * @example
 * const MemoizedComponent = memo(MyComponent, 
 *   createPropsComparator(['complexProp', 'anotherProp'])
 * );
 */
export function createPropsComparator<P extends object>(deepCompareProps: (keyof P)[] = []): (prevProps: P, nextProps: P) => boolean {
  return (prevProps: P, nextProps: P): boolean => {
    // First check for shallow equality (fast path)
    if (Object.is(prevProps, nextProps)) {
      return true;
    }

    // Check each shallow prop
    for (const key in prevProps) {
      // Skip deep compare props
      if (deepCompareProps.includes(key as keyof P)) {
        continue;
      }

      if (!Object.is(prevProps[key], nextProps[key])) {
        return false;
      }
    }

    // Now handle deep comparison props
    for (const key of deepCompareProps) {
      // Basic deep comparison for objects and arrays
      if (!deepEqual(prevProps[key], nextProps[key])) {
        return false;
      }
    }

    return true;
  };
}

/**
 * Simple deep equality check
 */
function deepEqual(a: any, b: any): boolean {
  // Handle primitive types and referential equality
  if (a === b) return true;

  // Handle null/undefined
  if (a == null || b == null) return a === b;

  // Different types
  if (typeof a !== typeof b) return false;

  // Handle Date objects
  if (a instanceof Date && b instanceof Date) {
    return a.getTime() === b.getTime();
  }

  // Handle arrays
  if (Array.isArray(a) && Array.isArray(b)) {
    if (a.length !== b.length) return false;
    for (let i = 0; i < a.length; i++) {
      if (!deepEqual(a[i], b[i])) return false;
    }
    return true;
  }

  // Handle objects
  if (typeof a === 'object' && typeof b === 'object') {
    const keysA = Object.keys(a);
    const keysB = Object.keys(b);

    if (keysA.length !== keysB.length) return false;

    for (const key of keysA) {
      if (!Object.prototype.hasOwnProperty.call(b, key)) return false;
      if (!deepEqual(a[key], b[key])) return false;
    }

    return true;
  }

  return false;
}

/**
 * Log component renders to help identify unnecessary re-renders
 * Use during development only
 * 
 * @example
 * export default withRenderLog(MyComponent);
 */
export function withRenderLog<P extends object>(
  Component: React.ComponentType<P>,
  options: { 
    name?: string;
    logProps?: boolean;
    logUpdates?: boolean;
  } = {}
): React.FC<P> {
  // Only apply in development
  if (process.env.NODE_ENV !== 'development') {
    return Component as React.FC<P>;
  }

  const {
    name = Component.displayName || Component.name || 'Component',
    logProps = false,
    logUpdates = true
  } = options;

  const WithRenderLog: React.FC<P> = (props) => {
    console.log(`%c📊 Rendering ${name}`, 'color: #3b82f6; font-weight: bold;');

    if (logProps) {
      console.log(`Props for ${name}:`, props);
    }

    return <Component {...props} />;
  };

  WithRenderLog.displayName = `WithRenderLog(${name})`;
  return WithRenderLog;
}

