import useNetwork from '../hooks/useNetwork';

interface OptimizedQuizRendererProps {
  quizId: string;
  initialData?: any;
  relatedQuizIds?: string[];
}

/**
 * OptimizedQuizRenderer Component (Enhanced version)
 * This component showcases all performance optimizations:
 * - Server-side rendering with initial data
 * - SWR for client-side data fetching with stale-while-revalidate
 * - Background prefetching for related quizzes with smart prioritization
 * - React.memo and useMemo for optimized rendering
 * - Deferred non-critical content loading
 * - Redis cache integration (in production)
 * - Performance metrics tracking
 * - Network-aware rendering
 */
const OptimizedQuizRenderer = ({
  quizId,
  initialData,
  relatedQuizIds = []
}: OptimizedQuizRendererProps) => {
  // Track component performance in development
  useRenderMetrics('OptimizedQuizRenderer');
  // Track render type for demonstration
  const [renderType, setRenderType] = useState<'ssr' | 'swr' | 'cache'>('ssr');

  // Get network status to optimize data loading
  const network = useNetwork();

  // Use SWR for client-side data fetching with optimized config based on network conditions
  const { data: quiz, error, isLoading, mutate } = useQuizSWR(quizId, undefined, {
    fallbackData: initialData,                                // Use server-rendered data as initial data
    revalidateOnFocus: false,                                 // Don't revalidate on window focus
    dedupingInterval: network.isSlow ? 30000 : 10000,         // Longer dedupe interval on slow networks
    keepPreviousData: true,                                   // Keep showing previous data while loading new data
    revalidateOnReconnect: !network.saveData,                 // Don't auto-revalidate in data saving mode
    errorRetryCount: network.online ? 3 : 0,                  // Don't retry on offline
    errorRetryInterval: network.isSlow ? 10000 : 5000,        // Longer retry interval on slow networks
    focusThrottleInterval: 10000                              // Throttle focus events
  });

  // Track if this is the first render with a more efficient approach
  useEffect(() => {
    // If we have initial data, it's SSR, else it's a client-side fetch
    setRenderType(initialData ? 'ssr' : 'swr');

    // After hydration, mark subsequent renders as from cache
    // Use requestAnimationFrame for better performance than setTimeout
    let isMounted = true;
    requestAnimationFrame(() => {
      if (isMounted) {
        // Wait for one more frame to ensure hydration is complete
        requestAnimationFrame(() => {
          if (isMounted) {
            setRenderType('cache');
          }
        });
      }
    });

    return () => {
      isMounted = false;
    };
  }, [initialData]);

  // Handle errors and loading states
  if (error) {
    return (
      <Card elevation="low" className="bg-red-50 border-red-200">
        <h3 className="text-red-700 font-medium">Error loading quiz</h3>
        <p className="text-red-600 mt-1">{error.message}</p>
        <Button 
          variant="outline" 
          className="mt-3" 
          onClick={() => mutate()}
          iconLeft={<RefreshIcon />}
        >
          Try Again
        </Button>
      </Card>
    );
  }

  if (isLoading) {
    return (
      <Card padding="medium" elevation="low" className="animate-pulse space-y-4">
        <div className="flex justify-center items-center py-4">
          <LoadingSpinner size="large" color="primary" />
        </div>
        <div className="h-8 bg-gray-200 rounded w-1/2"></div>
        <div className="h-32 bg-gray-200 rounded"></div>
        <div className="h-4 bg-gray-200 rounded w-1/4"></div>
      </Card>
    );
  }

  if (!quiz) {
    return (
      <Card elevation="low" className="bg-yellow-50 border-yellow-200">
        <h3 className="text-yellow-700 font-medium">Quiz not found</h3>
        <p className="text-yellow-600 mt-1">The requested quiz could not be found.</p>
      </Card>
    );
  }

  return (
    <ErrorBoundary>
      <Card elevation="medium" className="overflow-hidden" padding="none">
        {/* Debug info - remove in production */}
        <div className="bg-gray-100 px-4 py-2 text-xs font-mono flex justify-between items-center">
          <span className="flex items-center gap-1">
            <InfoIcon size={12} />
            Render: {renderType}
          </span>
          <Button 
            variant="primary" 
            size="small" 
            onClick={() => mutate()}
            iconLeft={<RefreshIcon size={12} />}
          >
            Refresh
          </Button>
        </div>

        {/* Quiz header */}
        <FadeIn duration={400} delay={200} className="px-6 py-4 border-b border-gray-200">
          <h1 className="text-2xl font-bold text-gray-800">{quiz.title}</h1>
          <div className="flex items-center mt-2 text-sm text-gray-600">
            <span className="inline-flex items-center rounded-md bg-blue-50 px-2 py-1 text-xs font-medium text-blue-700 ring-1 ring-inset ring-blue-700/10">
              {quiz.difficulty}
            </span>
            <span className="ml-3">{quiz.questions.length} Questions</span>
          </div>
          {quiz.description && (
            <p className="mt-4 text-gray-700">{quiz.description}</p>
          )}
        </FadeIn>

        {/* Quiz questions preview */}
        <FadeIn duration={400} delay={300} className="px-6 py-4">
          <h2 className="text-lg font-semibold mb-4">Questions Preview</h2>
          <div className="space-y-4">
            {quiz.questions.slice(0, 3).map((question: any, index: number) => (
              <Card 
                key={question.id} 
                elevation="low" 
                padding="medium" 
                className={tw(
                  "hover:shadow-md transition-shadow duration-200",
                  network.saveData ? "bg-gray-50" : undefined
                )}
              >
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium text-gray-500">Question {index + 1}</span>
                  <span className={twMerge(
                    "inline-flex items-center rounded-full bg-gray-50 px-2 py-1 text-xs font-medium text-gray-600 ring-1 ring-inset ring-gray-500/10",
                    question.type.includes("multiple") ? "bg-blue-50 text-blue-700 ring-blue-700/10" : ""
                  )}>
                    {question.type.replace('_', ' ')}
                  </span>
                </div>
                <p className="mt-2 text-gray-800">{question.question}</p>
              </Card>
            ))}

            {quiz.questions.length > 3 && (
              <div className="text-center py-2 text-gray-500 text-sm">
                +{quiz.questions.length - 3} more questions
              </div>
            )}
          </div>
        </FadeIn>

        {/* Related quizzes would go here */}
        {relatedQuizIds.length > 0 && (
          <FadeIn duration={400} delay={400} className="px-6 py-4 border-t border-gray-200">
            <h2 className="text-lg font-semibold mb-4">Related Quizzes</h2>
            <div className="grid grid-cols-2 gap-4">
              {relatedQuizIds.map((id) => (
                <Card 
                  key={id}
                  elevation="low"
                  padding="small"
                  className="hover:bg-gray-50 transition-colors duration-200 cursor-pointer"
                  data-prefetch-quiz-id={id} // Used by the QuizPrefetcher
                >
                  <span className="font-medium">{id}</span>
                </Card>
              ))}
            </div>
          </FadeIn>
        )}

        {/* Invisible prefetcher for related quizzes */}
        <QuizPrefetcher
          relatedQuizIds={relatedQuizIds}
          currentQuizId={quizId}
          enabled={true}
        />
      </Card>
    </ErrorBoundary>
  );
};

// Use our enhanced memo with proper naming and custom comparison
const EnhancedOptimizedQuizRenderer = MemoWithName(OptimizedQuizRenderer, 
  // Custom prop comparator that does deep comparison for initialData
  createPropsComparator<OptimizedQuizRendererProps>(['initialData'])
);

export default EnhancedOptimizedQuizRenderer;

'use client';

import { useCallback, useEffect, useMemo, useRef, memo } from 'react';
import { prefetchQuiz } from '../hooks/useQuizSWR';
import useNetwork from '../hooks/useNetwork';

interface QuizPrefetcherProps {
  relatedQuizIds: string[];
  currentQuizId?: string;
  prefetchDistance?: number;
  enabled?: boolean;
  prefetchPriority?: 'high' | 'low' | 'auto'; // New prop to control prefetching priority
}

/**
 * QuizPrefetcher component - Optimized version
 * This component prefetches related quiz data when the user is idle,
 * improving perceived performance when users navigate between quizzes.
 * 
 * Optimizations:
 * - Memoized filtering of quiz IDs
 * - Enhanced idle callback handling with priority
 * - Connection-aware prefetching using Network Information API
 * - Throttled prefetching to prevent network congestion
 * - Prefetching tracking to avoid duplicate requests
 */
const QuizPrefetcher = memo(({
  relatedQuizIds,
  currentQuizId,
  prefetchDistance = 400, // Default distance in pixels to start prefetching
  enabled = true,
  prefetchPriority = 'auto'
}: QuizPrefetcherProps) => {
  // Track which quizzes have been prefetched to avoid duplicates
  const prefetchedQuizIds = useRef<Set<string>>(new Set());

  // Track if the component is mounted
  const isMounted = useRef<boolean>(true);

  // Filter out current quiz from related quizzes - memoized for performance
  const quizzesToPrefetch = useMemo(() => 
    currentQuizId
      ? relatedQuizIds.filter(id => id !== currentQuizId && !prefetchedQuizIds.current.has(id))
      : relatedQuizIds.filter(id => !prefetchedQuizIds.current.has(id)),
    [relatedQuizIds, currentQuizId]
  );

  // Use our custom network hook to detect connection status
  const network = useNetwork();

  // Determine if we should prefetch based on network conditions
  const shouldPrefetch = useCallback(() => {
    // Don't prefetch if offline
    if (!network.online) return false;

    // Don't prefetch when data saver is enabled
    if (network.saveData) return false;

    // Don't prefetch on slow connections unless high priority
    if (network.isSlow && prefetchPriority !== 'high') return false;

    // On 3G connection, only prefetch high or auto priority
    if (network.effectiveConnectionType === '3g' && prefetchPriority === 'low') return false;

    return true;
  }, [network.online, network.saveData, network.isSlow, network.effectiveConnectionType, prefetchPriority]);

  // Perform the actual prefetch with tracking
  const performPrefetch = useCallback((quizId: string) => {
    if (!prefetchedQuizIds.current.has(quizId)) {
      prefetchQuiz(quizId);
      prefetchedQuizIds.current.add(quizId);
    }
  }, []);

  // Prefetch when the user is idle, with throttling
  const prefetchWhenIdle = useCallback(() => {
    if (typeof window === 'undefined' || !enabled || !quizzesToPrefetch.length || !shouldPrefetch()) return;

    // Use requestIdleCallback if available, or setTimeout as a fallback
    const requestIdleCallback = 
      window.requestIdleCallback || 
      ((cb) => setTimeout(cb, 1000));

    // Schedule prefetching during idle time
    requestIdleCallback(() => {
      if (!isMounted.current) return;

      // Limit number of prefetches to avoid bandwidth issues
      const batchSize = 2;
      const quizzesToLoad = quizzesToPrefetch.slice(0, batchSize);

      // Stagger the prefetching to avoid network congestion
      quizzesToLoad.forEach((quizId, index) => {
        setTimeout(() => {
          if (isMounted.current) {
            performPrefetch(quizId);
          }
        }, index * 300); // 300ms delay between each prefetch
      });
    }, { timeout: 2000 }); // 2 second timeout for idle callback
  }, [quizzesToPrefetch, enabled, shouldPrefetch, performPrefetch]);

  // Prefetch when elements are near viewport
  const setupIntersectionObserver = useCallback(() => {
    if (typeof window === 'undefined' || !enabled || !shouldPrefetch()) return;

    // Create targets for each related quiz
    const prefetchTargets = document.querySelectorAll('[data-prefetch-quiz-id]');

    if (!prefetchTargets.length) return;

    // Create intersection observer
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const quizId = entry.target.getAttribute('data-prefetch-quiz-id');
            if (quizId) {
              performPrefetch(quizId);
              // Unobserve after prefetching
              observer.unobserve(entry.target);
            }
          }
        });
      },
      { 
        rootMargin: `0px 0px ${prefetchDistance}px 0px`, // Start prefetching before elements enter viewport
        threshold: 0.1 // Start when 10% of the element is visible
      }
    );

    // Observe all prefetch targets
    prefetchTargets.forEach((target) => {
      observer.observe(target);
    });

    return () => observer.disconnect();
  }, [prefetchDistance, enabled, shouldPrefetch, performPrefetch]);

  useEffect(() => {
    // Don't prefetch if disabled or no related quizzes
    if (!enabled || !relatedQuizIds.length) return;

    // Initial prefetch when idle
    prefetchWhenIdle();

    // Set up intersection observer for scroll-based prefetching
    const cleanup = setupIntersectionObserver();

    return () => {
      isMounted.current = false;
      cleanup?.();
    };
  }, [prefetchWhenIdle, setupIntersectionObserver, enabled, relatedQuizIds.length]);

  // Don't render anything if disabled or no related quizzes
  if (!enabled || !relatedQuizIds.length) return null;

  // This component doesn't render anything
  return null;
});

QuizPrefetcher.displayName = 'QuizPrefetcher';
export default QuizPrefetcher;

"use client";

import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useResponsive } from '../hooks/useResponsive';

// Animation configuration based on device capabilities
const getAnimationConfig = (isMobile: boolean, isTablet: boolean, prefersReducedMotion: boolean) => {
  if (prefersReducedMotion) {
    return {
      duration: 0,
      easing: 'linear',
      stagger: 0,
      enableParallax: false,
      enableGestures: false
    };
  }

  if (isMobile) {
    return {
      duration: 200,
      easing: 'ease-out',
      stagger: 50,
      enableParallax: false,
      enableGestures: true
    };
  }

  if (isTablet) {
    return {
      duration: 300,
      easing: 'ease-out',
      stagger: 75,
      enableParallax: true,
      enableGestures: true
    };
  }

  return {
    duration: 400,
    easing: 'cubic-bezier(0.4, 0, 0.2, 1)',
    stagger: 100,
    enableParallax: true,
    enableGestures: true
  };
};

// Custom hooks for animations
export const useResponsiveAnimation = () => {
  const { isMobile, isTablet } = useResponsive();
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false);

  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setPrefersReducedMotion(mediaQuery.matches);

    const handler = (e: MediaQueryListEvent) => setPrefersReducedMotion(e.matches);
    mediaQuery.addEventListener('change', handler);
    return () => mediaQuery.removeEventListener('change', handler);
  }, []);

  return getAnimationConfig(isMobile, isTablet, prefersReducedMotion);
};

// Intersection Observer hook for scroll animations
export const useScrollAnimation = (
  options: { threshold?: number; rootMargin?: string } = { threshold: 0.1, rootMargin: '0px' }
) => {
  const [isVisible, setIsVisible] = useState(false);
  const elementRef = useRef<HTMLDivElement>(null);
  const animationConfig = useResponsiveAnimation();

  useEffect(() => {
    const element = elementRef.current;
    if (!element || animationConfig.duration === 0) return;

    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting) {
        setIsVisible(true);
        observer.unobserve(element);
      }
    }, options);

    observer.observe(element);
    return () => observer.disconnect();
  }, [options, animationConfig.duration]);

  return { elementRef, isVisible, animationConfig };
};

// Staggered children animation hook
export const useStaggeredAnimation = (childCount: number, delay = 0) => {
  const [visibleIndices, setVisibleIndices] = useState<Set<number>>(new Set());
  const animationConfig = useResponsiveAnimation();
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (animationConfig.duration === 0) {
      setVisibleIndices(new Set(Array.from({ length: childCount }, (_, i) => i)));
      return;
    }

    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting) {
        let currentIndex = 0;
        const interval = setInterval(() => {
          if (currentIndex < childCount) {
            setVisibleIndices(prev => new Set([...prev, currentIndex]));
            currentIndex++;
          } else {
            clearInterval(interval);
          }
        }, animationConfig.stagger);

        observer.unobserve(entry.target);
        return () => clearInterval(interval);
      }
    }, { threshold: 0.1 });

    const element = containerRef.current;
    if (element) {
      observer.observe(element);
    }

    return () => observer.disconnect();
  }, [childCount, animationConfig.stagger, animationConfig.duration]);

  return { containerRef, visibleIndices, animationConfig };
};

// Parallax hook for desktop devices
export const useParallax = (speed = 0.5, enabled = true) => {
  const [offset, setOffset] = useState(0);
  const elementRef = useRef<HTMLDivElement>(null);
  const animationConfig = useResponsiveAnimation();

  useEffect(() => {
    if (!enabled || !animationConfig.enableParallax || animationConfig.duration === 0) {
      return;
    }

    const handleScroll = () => {
      const element = elementRef.current;
      if (!element) return;

      const rect = element.getBoundingClientRect();
      const scrolled = window.pageYOffset;
      const parallax = scrolled * speed;

      setOffset(parallax);
    };

    // Throttle scroll events for performance
    let ticking = false;
    const throttledHandleScroll = () => {
      if (!ticking) {
        requestAnimationFrame(() => {
          handleScroll();
          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener('scroll', throttledHandleScroll, { passive: true });
    return () => window.removeEventListener('scroll', throttledHandleScroll);
  }, [speed, enabled, animationConfig.enableParallax, animationConfig.duration]);

  return { elementRef, offset };
};

// Gesture handling hook for touch devices
export const useGestures = () => {
  const [gesture, setGesture] = useState<{
    type: 'swipe' | 'pinch' | null;
    direction?: 'left' | 'right' | 'up' | 'down';
    scale?: number;
  }>({ type: null });

  const elementRef = useRef<HTMLDivElement>(null);
  const animationConfig = useResponsiveAnimation();

  useEffect(() => {
    if (!animationConfig.enableGestures) return;

    const element = elementRef.current;
    if (!element) return;

    let startX = 0, startY = 0, startDistance = 0;
    let isSwipe = false, isPinch = false;

    const handleTouchStart = (e: TouchEvent) => {
      if (e.touches.length === 1) {
        startX = e.touches[0].clientX;
        startY = e.touches[0].clientY;
        isSwipe = true;
      } else if (e.touches.length === 2) {
        const touch1 = e.touches[0];
        const touch2 = e.touches[1];
        startDistance = Math.sqrt(
          Math.pow(touch2.clientX - touch1.clientX, 2) +
          Math.pow(touch2.clientY - touch1.clientY, 2)
        );
        isPinch = true;
      }
    };

    const handleTouchMove = (e: TouchEvent) => {
      e.preventDefault();

      if (isSwipe && e.touches.length === 1) {
        const currentX = e.touches[0].clientX;
        const currentY = e.touches[0].clientY;
        const diffX = startX - currentX;
        const diffY = startY - currentY;

        if (Math.abs(diffX) > Math.abs(diffY) && Math.abs(diffX) > 50) {
          setGesture({
            type: 'swipe',
            direction: diffX > 0 ? 'left' : 'right'
          });
        } else if (Math.abs(diffY) > Math.abs(diffX) && Math.abs(diffY) > 50) {
          setGesture({
            type: 'swipe',
            direction: diffY > 0 ? 'up' : 'down'
          });
        }
      } else if (isPinch && e.touches.length === 2) {
        const touch1 = e.touches[0];
        const touch2 = e.touches[1];
        const currentDistance = Math.sqrt(
          Math.pow(touch2.clientX - touch1.clientX, 2) +
          Math.pow(touch2.clientY - touch1.clientY, 2)
        );
        const scale = currentDistance / startDistance;

        setGesture({
          type: 'pinch',
          scale
        });
      }
    };

    const handleTouchEnd = () => {
      isSwipe = false;
      isPinch = false;
      setTimeout(() => setGesture({ type: null }), 300);
    };

    element.addEventListener('touchstart', handleTouchStart, { passive: false });
    element.addEventListener('touchmove', handleTouchMove, { passive: false });
    element.addEventListener('touchend', handleTouchEnd);

    return () => {
      element.removeEventListener('touchstart', handleTouchStart);
      element.removeEventListener('touchmove', handleTouchMove);
      element.removeEventListener('touchend', handleTouchEnd);
    };
  }, [animationConfig.enableGestures]);

  return { elementRef, gesture };
};

// Performance monitoring hook
export const useAnimationPerformance = () => {
  const [metrics, setMetrics] = useState({
    fps: 60,
    frameDrops: 0,
    isSmooth: true
  });

  useEffect(() => {
    let frameCount = 0;
    let lastTime = performance.now();
    let frameDrops = 0;

    const measurePerformance = () => {
      const currentTime = performance.now();
      const deltaTime = currentTime - lastTime;

      if (deltaTime > 16.67 * 2) { // More than 2 frames at 60fps
        frameDrops++;
      }

      frameCount++;

      if (frameCount % 60 === 0) { // Update every 60 frames
        const fps = Math.round(1000 / (deltaTime / 60));
        const isSmooth = fps >= 55; // Consider smooth if above 55fps

        setMetrics({
          fps,
          frameDrops,
          isSmooth
        });
      }

      lastTime = currentTime;
      requestAnimationFrame(measurePerformance);
    };

    const animationId = requestAnimationFrame(measurePerformance);
    return () => cancelAnimationFrame(animationId);
  }, []);

  return metrics;
};

// Animation utility components
interface AnimatedElementProps {
  children: React.ReactNode;
  animation?: 'fadeIn' | 'slideUp' | 'slideIn' | 'scale' | 'rotate';
  delay?: number;
  className?: string;
}

export const AnimatedElement: React.FC<AnimatedElementProps> = ({
  children,
  animation = 'fadeIn',
  delay = 0,
  className = ''
}) => {
  const { elementRef, isVisible, animationConfig } = useScrollAnimation();

  const getAnimationClasses = () => {
    if (animationConfig.duration === 0) return '';

    const base = `transition-all ease-out`;
    const duration = `duration-${animationConfig.duration}`;

    if (!isVisible) {
      switch (animation) {
        case 'fadeIn':
          return `${base} ${duration} opacity-0`;
        case 'slideUp':
          return `${base} ${duration} opacity-0 translate-y-8`;
        case 'slideIn':
          return `${base} ${duration} opacity-0 translate-x-8`;
        case 'scale':
          return `${base} ${duration} opacity-0 scale-95`;
        case 'rotate':
          return `${base} ${duration} opacity-0 rotate-12`;
        default:
          return `${base} ${duration} opacity-0`;
      }
    }

    return `${base} ${duration} opacity-100 translate-y-0 translate-x-0 scale-100 rotate-0`;
  };

  const style = delay > 0 ? { transitionDelay: `${delay}ms` } : {};

  return (
    <div
      ref={elementRef}
      className={`${getAnimationClasses()} ${className}`}
      style={style}
    >
      {children}
    </div>
  );
};

interface StaggeredContainerProps {
  children: React.ReactNode;
  staggerDelay?: number;
  className?: string;
}

export const StaggeredContainer: React.FC<StaggeredContainerProps> = ({
  children,
  staggerDelay,
  className = ''
}) => {
  const childArray = React.Children.toArray(children);
  const { containerRef, visibleIndices, animationConfig } = useStaggeredAnimation(
    childArray.length,
    staggerDelay
  );

  return (
    <div ref={containerRef} className={className}>
      {childArray.map((child, index) => (
        <div
          key={index}
          className={`
            transition-all duration-${animationConfig.duration} ease-out
            ${visibleIndices.has(index) 
              ? 'opacity-100 translate-y-0' 
              : 'opacity-0 translate-y-4'
            }
          `}
          style={{
            transitionDelay: visibleIndices.has(index) 
              ? `${index * (staggerDelay || animationConfig.stagger)}ms` 
              : '0ms'
          }}
        >
          {child}
        </div>
      ))}
    </div>
  );
};

interface ParallaxContainerProps {
  children: React.ReactNode;
  speed?: number;
  className?: string;
}

export const ParallaxContainer: React.FC<ParallaxContainerProps> = ({
  children,
  speed = 0.5,
  className = ''
}) => {
  const { elementRef, offset } = useParallax(speed);

  return (
    <div
      ref={elementRef}
      className={className}
      style={{
        transform: `translateY(${offset}px)`,
        willChange: 'transform'
      }}
    >
      {children}
    </div>
  );
};

