import React, { useState, useEffect } from 'react';
import Image, { ImageProps } from 'next/image';
import { useResponsive } from '@/app/hooks/useResponsive';

interface ResponsiveImageSourceProps {
  mobile?: string;
  mobileLarge?: string;
  tablet?: string;
  desktop?: string;
  desktopLarge?: string;
  desktopXl?: string;
}

interface OptimizedResponsiveImageProps extends Omit<ImageProps, 'src'> {
  src: string;
  alt: string;
  sources?: ResponsiveImageSourceProps;
  quality?: number;
  loading?: 'lazy' | 'eager';
  blurDataURL?: string;
  placeholder?: 'blur' | 'empty';
  objectFit?: 'cover' | 'contain' | 'fill' | 'none' | 'scale-down';
  aspectRatio?: string;
  backgroundBlur?: boolean;
  fallbackSrc?: string;
}

/**
 * Optimized responsive image component with device-specific sources
 * Features:
 * - Device-specific image sources for optimal loading
 * - Automatic quality optimization based on device
 * - Progressive loading with blur placeholder
 * - Fallback handling for failed image loads
 * - Aspect ratio maintenance
 * - Performance monitoring
 */
export const OptimizedResponsiveImage: React.FC<OptimizedResponsiveImageProps> = ({
  src,
  alt,
  sources,
  quality,
  loading = 'lazy',
  blurDataURL,
  placeholder = 'blur',
  objectFit = 'cover',
  aspectRatio,
  backgroundBlur = false,
  fallbackSrc,
  className = '',
  ...props
}) => {
  const { deviceType, width, isTouchDevice, isBreakpointDown } = useResponsive();
  const [imageSrc, setImageSrc] = useState<string>(src);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);
  const [loadTime, setLoadTime] = useState<number | null>(null);

  // Determine optimal image source based on device
  const getOptimalImageSrc = () => {
    if (!sources) return src;

    switch (deviceType) {
      case 'mobile':
        return sources.mobile || src;
      case 'mobile-large':
        return sources.mobileLarge || sources.mobile || src;
      case 'tablet':
        return sources.tablet || sources.mobileLarge || src;
      case 'desktop':
        return sources.desktop || sources.tablet || src;
      case 'desktop-large':
        return sources.desktopLarge || sources.desktop || src;
      case 'desktop-xl':
        return sources.desktopXl || sources.desktopLarge || src;
      default:
        return src;
    }
  };

  // Determine optimal quality based on device and connection
  const getOptimalQuality = (): number => {
    if (quality) return quality;

    const connection = (navigator as any).connection;
    const effectiveType = connection?.effectiveType;

    // Base quality on device type and connection
    let baseQuality = 85; // Default high quality

    // Reduce quality for mobile devices
    if (deviceType === 'mobile' || deviceType === 'mobile-large') {
      baseQuality = 75;
    }

    // Further reduce quality for slow connections
    if (effectiveType) {
      switch (effectiveType) {
        case 'slow-2g':
        case '2g':
          baseQuality = Math.min(baseQuality - 30, 50);
          break;
        case '3g':
          baseQuality = Math.min(baseQuality - 15, 70);
          break;
        case '4g':
          // Keep base quality
          break;
      }
    }

    return baseQuality;
  };

  // Generate appropriate sizes attribute
  const getSizesAttribute = (): string => {
    if (props.sizes) return props.sizes;

    // Default responsive sizes based on common layouts
    return '(max-width: 576px) 100vw, (max-width: 768px) 50vw, (max-width: 992px) 33vw, 25vw';
  };

  // Handle image loading
  useEffect(() => {
    const optimalSrc = getOptimalImageSrc();
    if (optimalSrc !== imageSrc) {
      setImageSrc(optimalSrc);
      setImageLoaded(false);
      setImageError(false);
    }
  }, [deviceType, sources]);

  // Track image load performance
  const handleImageLoad = () => {
    setImageLoaded(true);
    setImageError(false);

    if (typeof window !== 'undefined' && window.performance) {
      const loadEndTime = performance.now();
      setLoadTime(loadEndTime);
    }
  };

  const handleImageError = () => {
    setImageError(true);
    if (fallbackSrc && imageSrc !== fallbackSrc) {
      setImageSrc(fallbackSrc);
    }
  };

  // Generate blur data URL if not provided
  const generateBlurDataURL = () => {
    if (blurDataURL) return blurDataURL;

    // Simple base64 encoded SVG blur placeholder
    const svg = `
      <svg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg">
        <rect width="40" height="40" className="fill-custom-gray-f0"/>
      </svg>
    `;
    return `data:image/svg+xml;base64,${btoa(svg)}`;
  };

  // Container styles with aspect ratio
  const containerStyles: React.CSSProperties = {
    ...(aspectRatio && { aspectRatio }),
    position: 'relative',
    overflow: 'hidden',
  };

  // Image wrapper styles for effects
  const imageWrapperStyles: React.CSSProperties = {
    transition: 'opacity 0.3s ease-in-out',
    opacity: imageLoaded ? 1 : 0.8,
    ...(backgroundBlur && !imageLoaded && {
      filter: 'blur(5px)',
      transform: 'scale(1.1)', // Prevent blur edge artifacts
    }),
  };

  const imageProps: ImageProps = {
    src: imageSrc,
    alt,
    quality: getOptimalQuality(),
    loading,
    sizes: getSizesAttribute(),
    placeholder: placeholder === 'blur' ? 'blur' : 'empty',
    blurDataURL: placeholder === 'blur' ? generateBlurDataURL() : undefined,
    onLoad: handleImageLoad,
    onError: handleImageError,
    style: {
      objectFit,
      width: '100%',
      height: '100%',
    },
    className: `responsive-image ${className}`,
    ...props,
  };

  return (
    <div style={containerStyles} className="responsive-image-container">
      <div style={imageWrapperStyles}>
        <Image {...imageProps} />
      </div>

      {/* Loading indicator for slow connections */}
      {!imageLoaded && !imageError && loading === 'lazy' && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
          <div className="animate-pulse bg-gray-300 rounded w-8 h-8"></div>
        </div>
      )}

      {/* Error state */}
      {imageError && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
          <div className="text-gray-500 text-sm text-center p-4">
            <div className="mb-2">📷</div>
            <div>Image failed to load</div>
          </div>
        </div>
      )}

      {/* Performance indicator (dev mode only) */}
      {process.env.NODE_ENV === 'development' && loadTime && (
        <div className="absolute top-2 right-2 bg-black/70 text-white text-xs px-2 py-1 rounded">
          {Math.round(loadTime)}ms
        </div>
      )}
    </div>
  );
};

interface LazyImageGridProps {
  images: Array<{
    id: string;
    src: string;
    alt: string;
    sources?: ResponsiveImageSourceProps;
  }>;
  columns?: {
    mobile?: number;
    tablet?: number;
    desktop?: number;
  };
  gap?: string;
  aspectRatio?: string;
}

/**
 * Optimized image grid with responsive layout and lazy loading
 */
export const LazyImageGrid: React.FC<LazyImageGridProps> = ({
  images,
  columns = { mobile: 1, tablet: 2, desktop: 3 },
  gap = 'gap-4',
  aspectRatio = '16/9',
}) => {
  const { deviceType } = useResponsive();

  const getColumnCount = () => {
    switch (deviceType) {
      case 'mobile':
      case 'mobile-large':
        return columns.mobile || 1;
      case 'tablet':
        return columns.tablet || 2;
      case 'desktop':
      case 'desktop-large':
      case 'desktop-xl':
        return columns.desktop || 3;
      default:
        return 2;
    }
  };

  const gridClasses = `grid grid-cols-${getColumnCount()} ${gap}`;

  return (
    <div className={gridClasses}>
      {images.map((image, index) => (
        <OptimizedResponsiveImage
          key={image.id}
          src={image.src}
          alt={image.alt}
          sources={image.sources}
          aspectRatio={aspectRatio}
          loading={index < 4 ? 'eager' : 'lazy'} // Load first 4 images eagerly
          placeholder="blur"
          className="w-full h-full object-cover rounded-lg"
        />
      ))}
    </div>
  );
};

/**
 * Hook for image preloading with device-specific optimization
 */
export const useImagePreloader = (images: string[]) => {
  const [loadedImages, setLoadedImages] = useState<Set<string>>(new Set());
  const [loadingImages, setLoadingImages] = useState<Set<string>>(new Set());
  const { deviceType } = useResponsive();

  const preloadImage = (src: string): Promise<void> => {
    return new Promise((resolve, reject) => {
      if (loadedImages.has(src)) {
        resolve();
        return;
      }

      setLoadingImages(prev => new Set(prev).add(src));

      const img = document.createElement('img');
      img.onload = () => {
        setLoadedImages(prev => new Set(prev).add(src));
        setLoadingImages(prev => {
          const next = new Set(prev);
          next.delete(src);
          return next;
        });
        resolve();
      };
      img.onerror = reject;
      img.src = src;
    });
  };

  const preloadImages = async (imagesToPreload: string[] = images) => {
    // Limit preloading on mobile devices to save bandwidth
    const maxPreload = deviceType === 'mobile' || deviceType === 'mobile-large' ? 3 : 6;
    const limitedImages = imagesToPreload.slice(0, maxPreload);

    try {
      await Promise.all(limitedImages.map(preloadImage));
    } catch (error) {
      console.warn('Some images failed to preload:', error);
    }
  };

  return {
    preloadImages,
    loadedImages,
    loadingImages,
    isImageLoaded: (src: string) => loadedImages.has(src),
    isImageLoading: (src: string) => loadingImages.has(src),
  };
};

export default OptimizedResponsiveImage;

'use client';

import React, { useState, useCallback, memo } from 'react';
import Image from 'next/image';
import useIntersectionObserver from '@/app/hooks/useIntersectionObserver';

interface LazyImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  className?: string;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  placeholder?: 'blur' | 'empty';
  blurDataURL?: string;
  onLoad?: () => void;
}

/**
 * LazyImage - A component that only loads images when they're about to enter the viewport
 * Implements efficient image loading from performance checklist
 * Optimized with memo and loading state management
 */
const LazyImage: React.FC<LazyImageProps> = memo(({
  src,
  alt,
  width,
  height,
  className = '',
  priority = false,
  quality,
  sizes,
  placeholder,
  blurDataURL,
  onLoad,
}) => {
  // Track if image has loaded successfully
  const [isLoaded, setIsLoaded] = useState<boolean>(false);
  const [isError, setIsError] = useState<boolean>(false);

  // Use our intersection observer hook with optimized settings
  // Images will only start loading when they're close to entering the viewport
  // freezeOnceVisible ensures we don't stop loading if image scrolls out of view
  const [ref, isInView] = useIntersectionObserver<HTMLDivElement>({
    rootMargin: '300px', // Increased to start loading when image is 300px from viewport for better UX
    threshold: 0.1,      // Start loading when at least 10% of the element is visible
    freezeOnceVisible: true, // Once we start loading, don't stop
    initialInView: priority, // If priority is true, treat as initially in view
    skipObserver: priority,  // Skip the observer if priority is true
  });

  // Handle image load event
  const handleImageLoad = useCallback(() => {
    setIsLoaded(true);
    if (onLoad) onLoad();
  }, [onLoad]);

  // Handle image error
  const handleImageError = useCallback(() => {
    setIsError(true);
  }, []);

  return (
    <div 
      ref={ref} 
      className={`relative overflow-hidden ${className}`}
      style={{ width, height }}
    >
      {(isInView || priority) && (
        <Image
          src={src}
          alt={alt}
          width={width}
          height={height}
          className={`${className} ${isLoaded ? 'opacity-100' : 'opacity-0'} transition-opacity duration-300`}
          priority={priority}
          quality={quality || 80} // Set a default quality value
          sizes={sizes}
          placeholder={placeholder}
          blurDataURL={blurDataURL}
          onLoad={handleImageLoad}
          onError={handleImageError}
          loading={priority ? 'eager' : 'lazy'}
        />
      )}

      {/* Show placeholder until image is loaded or if there's an error */}
      {(!isInView || !isLoaded) && !priority && (
        <div className="absolute inset-0 bg-gray-200 animate-pulse" />
      )}

      {/* Show error state if image failed to load */}
      {isError && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-100 text-gray-500">
          <span className="text-sm">Failed to load image</span>
        </div>
      )}
    </div>
  );
});

// Add display name for debugging
LazyImage.displayName = 'LazyImage'; 

export default LazyImage;

'use client';

import React, { useEffect, useState } from 'react';
import { useResponsive } from '@/app/hooks/useResponsive';

interface PerformanceMetrics {
  fcp: number | null; // First Contentful Paint
  lcp: number | null; // Largest Contentful Paint
  cls: number | null; // Cumulative Layout Shift
  fid: number | null; // First Input Delay
  ttfb: number | null; // Time to First Byte
}

interface ResponsivePerformanceData {
  deviceType: string;
  viewport: string;
  metrics: PerformanceMetrics;
  memoryUsage?: any;
  connectionType?: string;
}

/**
 * Performance monitoring component for responsive design
 * Tracks Core Web Vitals and device-specific performance metrics
 */
export const PerformanceMonitor: React.FC<{ showDetails?: boolean }> = ({ 
  showDetails = false 
}) => {
  const { deviceType, breakpoint, width, height } = useResponsive();
  const [metrics, setMetrics] = useState<PerformanceMetrics>({
    fcp: null,
    lcp: null,
    cls: null,
    fid: null,
    ttfb: null,
  });
  const [performanceData, setPerformanceData] = useState<ResponsivePerformanceData | null>(null);

  useEffect(() => {
    // Performance Observer for Core Web Vitals
    if (typeof window !== 'undefined' && 'PerformanceObserver' in window) {
      // First Contentful Paint
      const fcpObserver = new PerformanceObserver((entryList) => {
        const entries = entryList.getEntries();
        const fcpEntry = entries.find(entry => entry.name === 'first-contentful-paint');
        if (fcpEntry) {
          setMetrics(prev => ({ ...prev, fcp: fcpEntry.startTime }));
        }
      });

      // Largest Contentful Paint
      const lcpObserver = new PerformanceObserver((entryList) => {
        const entries = entryList.getEntries();
        const lcpEntry = entries[entries.length - 1];
        if (lcpEntry) {
          setMetrics(prev => ({ ...prev, lcp: lcpEntry.startTime }));
        }
      });

      // Cumulative Layout Shift
      const clsObserver = new PerformanceObserver((entryList) => {
        let clsValue = 0;
        const entries = entryList.getEntries();
        entries.forEach((entry: any) => {
          if (!entry.hadRecentInput) {
            clsValue += entry.value;
          }
        });
        setMetrics(prev => ({ ...prev, cls: clsValue }));
      });

      // First Input Delay
      const fidObserver = new PerformanceObserver((entryList) => {
        const entries = entryList.getEntries();
        const fidEntry = entries[0];
        if (fidEntry) {
          setMetrics(prev => ({ 
            ...prev, 
            fid: (fidEntry as any).processingStart - fidEntry.startTime 
          }));
        }
      });

      try {
        fcpObserver.observe({ type: 'paint', buffered: true });
        lcpObserver.observe({ type: 'largest-contentful-paint', buffered: true });
        clsObserver.observe({ type: 'layout-shift', buffered: true });
        fidObserver.observe({ type: 'first-input', buffered: true });
      } catch (error) {
        console.warn('Performance monitoring not fully supported:', error);
      }

      // Navigation timing for TTFB
      if (window.performance.timing) {
        const ttfb = window.performance.timing.responseStart - window.performance.timing.navigationStart;
        setMetrics(prev => ({ ...prev, ttfb }));
      }

      return () => {
        fcpObserver.disconnect();
        lcpObserver.disconnect();
        clsObserver.disconnect();
        fidObserver.disconnect();
      };
    }
  }, []);

  useEffect(() => {
    // Update performance data when device changes
    const data: ResponsivePerformanceData = {
      deviceType,
      viewport: `${width}x${height}`,
      metrics,
      memoryUsage: (navigator as any).memory ? {
        used: Math.round((navigator as any).memory.usedJSHeapSize / 1048576),
        total: Math.round((navigator as any).memory.totalJSHeapSize / 1048576),
        limit: Math.round((navigator as any).memory.jsHeapSizeLimit / 1048576),
      } : undefined,
      connectionType: (navigator as any).connection?.effectiveType || 'unknown'
    };

    setPerformanceData(data);
  }, [deviceType, width, height, metrics]);

  // Helper function to get performance score color
  const getScoreColor = (metric: string, value: number | null): string => {
    if (value === null) return 'text-gray-400';

    switch (metric) {
      case 'fcp':
        return value <= 1800 ? 'text-green-500' : value <= 3000 ? 'text-yellow-500' : 'text-red-500';
      case 'lcp':
        return value <= 2500 ? 'text-green-500' : value <= 4000 ? 'text-yellow-500' : 'text-red-500';
      case 'cls':
        return value <= 0.1 ? 'text-green-500' : value <= 0.25 ? 'text-yellow-500' : 'text-red-500';
      case 'fid':
        return value <= 100 ? 'text-green-500' : value <= 300 ? 'text-yellow-500' : 'text-red-500';
      case 'ttfb':
        return value <= 800 ? 'text-green-500' : value <= 1800 ? 'text-yellow-500' : 'text-red-500';
      default:
        return 'text-gray-400';
    }
  };

  if (!showDetails) return null;

  return (
    <div className="fixed bottom-4 right-4 bg-black/80 backdrop-blur-sm text-white p-4 rounded-lg text-xs max-w-sm z-50">
      <h4 className="font-bold mb-3 text-yellow-400">Performance Monitor</h4>

      {/* Device Info */}
      <div className="mb-3 pb-2 border-b border-gray-600">
        <div>Device: <span className="text-blue-400">{performanceData?.deviceType}</span></div>
        <div>Viewport: <span className="text-blue-400">{performanceData?.viewport}</span></div>
        <div>Breakpoint: <span className="text-blue-400">{breakpoint}</span></div>
        {performanceData?.connectionType && (
          <div>Connection: <span className="text-blue-400">{performanceData.connectionType}</span></div>
        )}
      </div>

      {/* Core Web Vitals */}
      <div className="space-y-1">
        <div className="font-semibold text-green-400 mb-2">Core Web Vitals:</div>

        <div className="flex justify-between">
          <span>FCP:</span>
          <span className={getScoreColor('fcp', metrics.fcp)}>
            {metrics.fcp ? `${Math.round(metrics.fcp)}ms` : 'Loading...'}
          </span>
        </div>

        <div className="flex justify-between">
          <span>LCP:</span>
          <span className={getScoreColor('lcp', metrics.lcp)}>
            {metrics.lcp ? `${Math.round(metrics.lcp)}ms` : 'Loading...'}
          </span>
        </div>

        <div className="flex justify-between">
          <span>CLS:</span>
          <span className={getScoreColor('cls', metrics.cls)}>
            {metrics.cls !== null ? metrics.cls.toFixed(3) : 'Loading...'}
          </span>
        </div>

        <div className="flex justify-between">
          <span>FID:</span>
          <span className={getScoreColor('fid', metrics.fid)}>
            {metrics.fid ? `${Math.round(metrics.fid)}ms` : 'Waiting...'}
          </span>
        </div>

        <div className="flex justify-between">
          <span>TTFB:</span>
          <span className={getScoreColor('ttfb', metrics.ttfb)}>
            {metrics.ttfb ? `${Math.round(metrics.ttfb)}ms` : 'Loading...'}
          </span>
        </div>
      </div>

      {/* Memory Usage */}
      {performanceData?.memoryUsage && (
        <div className="mt-3 pt-2 border-t border-gray-600">
          <div className="font-semibold text-purple-400 mb-1">Memory (MB):</div>
          <div className="flex justify-between text-xs">
            <span>Used:</span>
            <span className="text-purple-300">{performanceData.memoryUsage.used}</span>
          </div>
          <div className="flex justify-between text-xs">
            <span>Total:</span>
            <span className="text-purple-300">{performanceData.memoryUsage.total}</span>
          </div>
        </div>
      )}
    </div>
  );
};

/**
 * Hook for tracking responsive performance metrics
 */
export const usePerformanceTracking = () => {
  const { deviceType, breakpoint } = useResponsive();
  const [performanceScore, setPerformanceScore] = useState<number>(0);

  useEffect(() => {
    // Calculate a simple performance score based on device type
    const calculateScore = () => {
      let score = 100;

      // Penalize mobile devices less as they're expected to be slower
      if (deviceType === 'mobile' || deviceType === 'mobile-large') {
        score = Math.max(score - 10, 0);
      }

      // Consider memory usage if available
      if ((navigator as any).memory) {
        const memoryUsage = (navigator as any).memory.usedJSHeapSize / (navigator as any).memory.jsHeapSizeLimit;
        if (memoryUsage > 0.8) score = Math.max(score - 20, 0);
        else if (memoryUsage > 0.6) score = Math.max(score - 10, 0);
      }

      // Consider connection type
      const connection = (navigator as any).connection;
      if (connection) {
        switch (connection.effectiveType) {
          case 'slow-2g':
          case '2g':
            score = Math.max(score - 30, 0);
            break;
          case '3g':
            score = Math.max(score - 15, 0);
            break;
          case '4g':
            // No penalty
            break;
        }
      }

      setPerformanceScore(score);
    };

    calculateScore();
  }, [deviceType, breakpoint]);

  return {
    performanceScore,
    deviceType,
    breakpoint,
  };
};

export default PerformanceMonitor;

'use client';

import React from 'react';
import { useResponsive, BreakpointKey, DeviceType, ExtendedDeviceType } from '@/app/hooks/useResponsive';

interface ResponsiveProps {
  children: React.ReactNode;
}

interface BreakpointProps extends ResponsiveProps {
  breakpoint: BreakpointKey;
  direction?: 'up' | 'down';
}

interface DeviceProps extends ResponsiveProps {
  device: ExtendedDeviceType | ExtendedDeviceType[];
}

/**
 * Component that conditionally renders children based on breakpoint
 * 
 * @example
 * <ShowOn breakpoint="md" direction="up">
 *   <DesktopNavigation />
 * </ShowOn>
 */
export const ShowOn: React.FC<BreakpointProps> = ({ 
  children, 
  breakpoint, 
  direction = 'up' 
}) => {
  const { isBreakpointUp, isBreakpointDown } = useResponsive();

  const shouldShow = direction === 'up' 
    ? isBreakpointUp(breakpoint)
    : isBreakpointDown(breakpoint);

  return shouldShow ? <>{children}</> : null;
};

/**
 * Component that conditionally hides children based on breakpoint
 * 
 * @example
 * <HideOn breakpoint="md" direction="down">
 *   <MobileOnlyComponent />
 * </HideOn>
 */
export const HideOn: React.FC<BreakpointProps> = ({ 
  children, 
  breakpoint, 
  direction = 'up' 
}) => {
  const { isBreakpointUp, isBreakpointDown } = useResponsive();

  const shouldHide = direction === 'up' 
    ? isBreakpointUp(breakpoint)
    : isBreakpointDown(breakpoint);

  return !shouldHide ? <>{children}</> : null;
};

/**
 * Component that renders children only on specific device types
 * 
 * @example
 * <ShowOnDevice device="mobile">
 *   <MobileHeader />
 * </ShowOnDevice>
 * 
 * <ShowOnDevice device={["tablet", "desktop"]}>
 *   <DesktopTabletView />
 * </ShowOnDevice>
 */
export const ShowOnDevice: React.FC<DeviceProps> = ({ children, device }) => {
  const { deviceType } = useResponsive();

  const devices = Array.isArray(device) ? device : [device];
  const shouldShow = devices.includes(deviceType);

  return shouldShow ? <>{children}</> : null;
};

/**
 * Component that hides children on specific device types
 */
export const HideOnDevice: React.FC<DeviceProps> = ({ children, device }) => {
  const { deviceType } = useResponsive();

  const devices = Array.isArray(device) ? device : [device];
  const shouldHide = devices.includes(deviceType);

  return !shouldHide ? <>{children}</> : null;
};

/**
 * Mobile-only component wrapper
 */
export const MobileOnly: React.FC<ResponsiveProps> = ({ children }) => (
  <ShowOnDevice device={["mobile", "mobile-large"]}>{children}</ShowOnDevice>
);

/**
 * Tablet-only component wrapper
 */
export const TabletOnly: React.FC<ResponsiveProps> = ({ children }) => (
  <ShowOnDevice device="tablet">{children}</ShowOnDevice>
);

/**
 * Desktop-only component wrapper (includes all desktop sizes)
 */
export const DesktopOnly: React.FC<ResponsiveProps> = ({ children }) => (
  <ShowOnDevice device={["desktop", "desktop-large", "desktop-xl"]}>{children}</ShowOnDevice>
);

/**
 * Mobile and tablet wrapper
 */
export const MobileTablet: React.FC<ResponsiveProps> = ({ children }) => (
  <ShowOnDevice device={["mobile", "mobile-large", "tablet"]}>{children}</ShowOnDevice>
);

/**
 * Tablet and desktop wrapper
 */
export const TabletDesktop: React.FC<ResponsiveProps> = ({ children }) => (
  <ShowOnDevice device={["tablet", "desktop", "desktop-large", "desktop-xl"]}>{children}</ShowOnDevice>
);

interface ResponsiveImageProps {
  src: string;
  alt: string;
  className?: string;
  mobileSrc?: string;
  tabletSrc?: string;
  desktopSrc?: string;
  sizes?: string;
  priority?: boolean;
}

/**
 * Responsive image component with device-specific sources
 * 
 * @example
 * <ResponsiveImage
 *   src="/images/hero-desktop.jpg"
 *   mobileSrc="/images/hero-mobile.jpg"
 *   tabletSrc="/images/hero-tablet.jpg"
 *   alt="Hero image"
 *   sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
 * />
 */
export const ResponsiveImage: React.FC<ResponsiveImageProps> = ({
  src,
  alt,
  className = '',
  mobileSrc,
  tabletSrc,
  desktopSrc,
  sizes = '100vw',
  priority = false,
}) => {
  const { deviceType } = useResponsive();

  const getImageSrc = () => {
    switch (deviceType) {
      case 'mobile':
      case 'mobile-large':
        return mobileSrc || src;
      case 'tablet':
        return tabletSrc || src;
      case 'desktop':
      case 'desktop-large':
      case 'desktop-xl':
        return desktopSrc || src;
      default:
        return src;
    }
  };

  return (
    <img
      src={getImageSrc()}
      alt={alt}
      className={`responsive-image ${className}`}
      sizes={sizes}
      loading={priority ? undefined : 'lazy'}
    />
  );
};

interface ResponsiveContainerProps {
  children: React.ReactNode;
  className?: string;
  mobileClass?: string;
  tabletClass?: string;
  desktopClass?: string;
}

/**
 * Container component with device-specific styling
 * 
 * @example
 * <ResponsiveContainer
 *   className="base-styles"
 *   mobileClass="px-4 py-2"
 *   tabletClass="px-6 py-4"
 *   desktopClass="px-8 py-6"
 * >
 *   <Content />
 * </ResponsiveContainer>
 */
export const ResponsiveContainer: React.FC<ResponsiveContainerProps> = ({
  children,
  className = '',
  mobileClass = '',
  tabletClass = '',
  desktopClass = '',
}) => {
  const { getResponsiveClasses } = useResponsive();

  const responsiveClasses = getResponsiveClasses({
    mobile: mobileClass,
    'mobile-large': mobileClass,
    tablet: tabletClass,
    desktop: desktopClass,
    'desktop-large': desktopClass,
    'desktop-xl': desktopClass,
  });

  return (
    <div className={`${className} ${responsiveClasses}`}>
      {children}
    </div>
  );
};

interface ResponsiveGridProps {
  children: React.ReactNode;
  className?: string;
  mobileCols?: number;
  tabletCols?: number;
  desktopCols?: number;
  gap?: string;
}

/**
 * Responsive grid component with device-specific column counts
 * 
 * @example
 * <ResponsiveGrid
 *   mobileCols={1}
 *   tabletCols={2}
 *   desktopCols={3}
 *   gap="gap-4"
