    tablet: `grid-cols-${tabletCols}`,
    desktop: `grid-cols-${desktopCols}`,
    'desktop-large': `grid-cols-${desktopCols}`,
    'desktop-xl': `grid-cols-${desktopCols}`,
  });

  return (
    <div className={`grid ${gridClasses} ${gap} ${className}`}>
      {children}
    </div>
  );
};

