// app/hooks/useIntersectionObserver.ts
import { useState, useEffect, useRef, useCallback } from 'react';

interface IntersectionOptions extends IntersectionObserverInit {
  freezeOnceVisible?: boolean;
  initialInView?: boolean; // New option to set initial state for SSR
  skipObserver?: boolean; // Option to skip observer (for testing or force values)
  observerOnce?: boolean; // Only trigger once and then disconnect
}

/**
 * An optimized hook to detect when an element enters or leaves the viewport
 * Implements efficient lazy loading pattern from performance checklist
 * 
 * @param options IntersectionObserver options and additional config
 * @returns [ref, isIntersecting, entry] - Attach ref to the element you want to observe
 */
function useIntersectionObserver<T extends Element>({
  threshold = 0,
  root = null, 
  rootMargin = '0%',
  freezeOnceVisible = false,
  initialInView = false, // Default for SSR is false
  skipObserver = false, // Default is to use the observer
  observerOnce = false  // Default is to keep observing
}: IntersectionOptions = {}) {
  const [entry, setEntry] = useState<IntersectionObserverEntry | null>(null);
  const [isInView, setIsInView] = useState(initialInView);
  const elementRef = useRef<T | null>(null);

  // Store frozen state in a ref to avoid re-creating the observer
  const frozen = useRef<boolean>(false);
  const observer = useRef<IntersectionObserver | null>(null);

  const updateEntry = useCallback(([e]: IntersectionObserverEntry[]): void => {
    setEntry(e);

    // Update frozen state if needed
    if (e.isIntersecting && freezeOnceVisible) {
      frozen.current = true;

      // If we only need to observe once, disconnect after it's in view
      if (observerOnce && observer.current) {
        observer.current.disconnect();
      }
    }

    // Update the isInView state
    setIsInView(e.isIntersecting || frozen.current);
  }, [freezeOnceVisible, observerOnce]);

  useEffect(() => {
    // Skip if told to or missing browser API
    if (skipObserver || typeof IntersectionObserver !== 'function') {
      return;
    }

    const node = elementRef.current;

    // Don't observe if element doesn't exist or if frozen and already seen
    if (!node || (freezeOnceVisible && frozen.current)) {
      return;
    }

    // Cleanup any existing observer
    if (observer.current) {
      observer.current.disconnect();
    }

    // Create the observer with memoized update function
    observer.current = new IntersectionObserver(
      updateEntry, 
      { threshold, root, rootMargin }
    );

    // Start observing
    observer.current.observe(node);

    // Clean up when component unmounts or deps change
    return () => {
      if (observer.current) {
        observer.current.disconnect();
      }
    };
  }, [threshold, root, rootMargin, freezeOnceVisible, skipObserver, updateEntry]);

  return [elementRef, isInView, entry] as const;
}

export default useIntersectionObserver;

'use client';

import { useState, useEffect, useCallback, useMemo } from 'react';

export type NetworkStatus = 'online' | 'offline' | 'slow-2g' | '2g' | '3g' | '4g' | 'unknown';

export interface UseNetworkResult {
  online: boolean;
  effectiveConnectionType: NetworkStatus;
  downlink: number | null;
  rtt: number | null;
  saveData: boolean | null;
  unsupported: boolean;
  isMetered: boolean | null;
  isSlow: boolean;
}

/**
 * Custom hook to monitor network connectivity and connection quality
 * Uses Network Information API when available, falls back to basic online/offline detection
 */
export default function useNetwork(): UseNetworkResult {
  // Check if we're in a browser environment
  const isBrowser = typeof window !== 'undefined';

  // Check if Network Information API is supported
  // @ts-ignore - Connection property not in all browsers
  const connection = isBrowser ? (navigator.connection || navigator.mozConnection || navigator.webkitConnection) : null;
  const unsupported = !connection;

  // State for network status
  const [online, setOnline] = useState<boolean>(isBrowser ? navigator.onLine : true);
  const [effectiveConnectionType, setEffectiveConnectionType] = useState<NetworkStatus>(
    // @ts-ignore - effectiveType not in all browsers
    connection?.effectiveType || 'unknown'
  );
  const [downlink, setDownlink] = useState<number | null>(connection?.downlink || null);
  const [rtt, setRtt] = useState<number | null>(connection?.rtt || null);
  const [saveData, setSaveData] = useState<boolean | null>(connection?.saveData || null);
  const [isMetered, setIsMetered] = useState<boolean | null>(connection?.metered || null);

  // Calculate if the connection is slow
  const isSlow = useMemo(() => {
    // Consider slow connection if:
    // - Offline
    // - Explicitly using slow connection (slow-2g, 2g)
    // - High RTT (>500ms)
    // - Low downlink (<1Mbps)
    // - Data saver mode is on
    return !online || 
           effectiveConnectionType === 'slow-2g' || 
           effectiveConnectionType === '2g' ||
           (rtt !== null && rtt > 500) ||
           (downlink !== null && downlink < 1) ||
           !!saveData;
  }, [online, effectiveConnectionType, rtt, downlink, saveData]);

  // Update network state
  const updateNetworkStatus = useCallback(() => {
    if (!isBrowser) return;

    setOnline(navigator.onLine);

    if (connection) {
      // @ts-ignore - effectiveType not in all browsers
      setEffectiveConnectionType(connection.effectiveType || 'unknown');
      setDownlink(connection.downlink || null);
      setRtt(connection.rtt || null);
      setSaveData(connection.saveData || false);
      setIsMetered(connection.metered || false);
    }
  }, [connection, isBrowser]);

  useEffect(() => {
    if (!isBrowser) return;

    // Initialize with current status
    updateNetworkStatus();

    // Set up event listeners for online/offline events
    window.addEventListener('online', updateNetworkStatus);
    window.addEventListener('offline', updateNetworkStatus);

    // Set up event listener for connection changes if supported
    if (connection) {
      connection.addEventListener('change', updateNetworkStatus);
    }

    return () => {
      // Clean up event listeners
      window.removeEventListener('online', updateNetworkStatus);
      window.removeEventListener('offline', updateNetworkStatus);

      if (connection) {
        connection.removeEventListener('change', updateNetworkStatus);
      }
    };
  }, [updateNetworkStatus, isBrowser, connection]);

  return {
    online,
    effectiveConnectionType,
    downlink,
    rtt,
    saveData,
    unsupported,
    isMetered,
    isSlow
  };
}

'use client';

import React, { useEffect, useRef, useState } from 'react';

interface RenderMetrics {
  renders: number;
  lastRenderTime: number;
  averageRenderTime: number;
  totalRenderTime: number;
}

/**
 * Custom hook for tracking component render performance
 * @returns A debug object with render metrics
 */
export default function useRenderMetrics(componentName: string): void {
  const renderCount = useRef(0);
  const lastRenderTimeRef = useRef(performance.now());
  const metricsRef = useRef<RenderMetrics>({
    renders: 0,
    lastRenderTime: 0,
    averageRenderTime: 0,
    totalRenderTime: 0
  });

  const isProduction = process.env.NODE_ENV === 'production';

  // Track render count
  renderCount.current += 1;
  const currentRender = renderCount.current;

  useEffect(() => {
    // Only perform measurements in development
    if (isProduction) {
      return;
    }

    // Measure time since component rendered
    const endTime = performance.now();
    const renderTime = endTime - lastRenderTimeRef.current;

    // Update metrics
    const metrics = metricsRef.current;
    metrics.renders = currentRender;
    metrics.lastRenderTime = renderTime;
    metrics.totalRenderTime += renderTime;
    metrics.averageRenderTime = metrics.totalRenderTime / currentRender;

    // Log to console on significant render time (> 10ms)
    if (renderTime > 10) {
      console.log(
        `%c${componentName} rendered in ${renderTime.toFixed(2)}ms (render #${currentRender})`,
        'color: #ff9800; font-weight: bold;'
      );
    }

    // Store metrics in global object for browser console access
    if (typeof window !== 'undefined') {
      // @ts-ignore
      window.__RENDER_METRICS__ = window.__RENDER_METRICS__ || {};
      // @ts-ignore
      window.__RENDER_METRICS__[componentName] = metrics;
    }
  });

  // Reset timer for next render
  lastRenderTimeRef.current = performance.now();
}

/**
 * Higher-order component wrapper for tracking render performance
 */
export function withRenderMetrics<P extends object>(
  Component: React.ComponentType<P>,
  options: { name?: string } = {}
) {
  const displayName = options.name || Component.displayName || Component.name;

  const WrappedComponent = (props: P) => {
    useRenderMetrics(displayName);
    return <Component {...props} />;
  };

  WrappedComponent.displayName = `WithRenderMetrics(${displayName})`;
  return WrappedComponent;
}

import useSWR, { SWRConfiguration, SWRResponse } from 'swr';
import { Quiz } from '../types/quiz';

export type UseQuizSWRResponse = SWRResponse<Quiz | null, Error> & {
  isLoading: boolean;
}

/**
 * Custom SWR hook for fetching quiz data with Stale-While-Revalidate pattern
 * This provides optimistic UI updates with background revalidation
 * 
 * @param quizId The ID of the quiz to fetch
 * @param questionType Optional filter for question type
 * @param options SWR configuration options
 * @returns SWR response with quiz data and loading state
 */
export function useQuizSWR(
  quizId?: string,
  questionType?: string,
  options?: SWRConfiguration
): UseQuizSWRResponse {
  // Only fetch if we have a quizId
  const shouldFetch = !!quizId;

  // Build the API URL with optional question type filter
  const apiUrl = shouldFetch 
    ? `/api/quiz/cached/${quizId}${questionType ? `?type=${questionType}` : ''}`
    : null;

  // Set default SWR options
  const defaultOptions: SWRConfiguration = {
    revalidateIfStale: true,
    revalidateOnFocus: true,
    revalidateOnReconnect: true,
    dedupingInterval: 5000, // 5 seconds deduplication interval
    ...options
  };

  // Use SWR to fetch the quiz data
  const swr = useSWR<Quiz | null, Error>(
    apiUrl,
    async (url) => {
      const res = await fetch(url);

      if (!res.ok) {
        // Handle specific error codes
        if (res.status === 404) {
          console.warn(`Quiz with ID '${quizId}' not found.`);
          return null;
        }

        // Handle other errors
        const errorData = await res.json();
        throw new Error(errorData.error || `Error fetching quiz: ${res.statusText}`);
      }

      return res.json();
    },
    defaultOptions
  );

  // Add isLoading helper
  const isLoading = shouldFetch && !swr.error && !swr.data;

  return {
    ...swr,
    isLoading
  };
}

/**
 * Prefetch quiz data to populate the cache
 * This can be used for preloading data for quizzes that the user might visit next
 * 
 * @param quizId The ID of the quiz to prefetch
 * @param questionType Optional filter for question type
 */
export async function prefetchQuiz(quizId: string, questionType?: string): Promise<void> {
  const apiUrl = `/api/quiz/cached/${quizId}${questionType ? `?type=${questionType}` : ''}`;

  try {
    // Fire and forget - we don't need to wait for the response
    fetch(apiUrl, {
      priority: 'low', // Use low priority for prefetching
      cache: 'force-cache' // Ensure we use the cache if available
    }).catch(() => {
      // Silently ignore prefetch errors
    });

    console.debug(`Prefetching quiz: ${quizId}`);
  } catch (error) {
    // Silently ignore prefetch errors
    console.debug(`Failed to prefetch quiz: ${quizId}`);
  }
}

'use client';

import { useState, useEffect, useCallback } from 'react';

interface UseAnimationFrameOptions {
  duration?: number;
  delay?: number;
  easing?: (t: number) => number;
  onComplete?: () => void;
}

/**
 * Custom hook for smooth animations using requestAnimationFrame
 * Returns a progress value between 0 and 1
 */
export default function useAnimationFrame({
  duration = 300,
  delay = 0,
  easing = t => t,
  onComplete
}: UseAnimationFrameOptions = {}) {
  const [progress, setProgress] = useState(0);
  const [isRunning, setIsRunning] = useState(false);

  const animate = useCallback(() => {
    setIsRunning(true);

    // Track animation start time
    let startTime = performance.now();
    let animationFrameId: number;
    let delayTimeout: NodeJS.Timeout | null = null;

    // Function to run the animation loop
    const runAnimation = (currentTime: number) => {
      // Calculate how much time has passed
      const elapsed = currentTime - startTime;

      // Calculate progress as a value between 0 and 1
      const rawProgress = Math.min(elapsed / duration, 1);

      // Apply easing function if provided
      const easedProgress = easing(rawProgress);

      // Update state with new progress
      setProgress(easedProgress);

      // Continue the animation if not complete
      if (rawProgress < 1) {
        animationFrameId = requestAnimationFrame(runAnimation);
      } else {
        // Animation is complete
        setIsRunning(false);
        onComplete?.();
      }
    };

    // Start animation after delay
    if (delay) {
      delayTimeout = setTimeout(() => {
        startTime = performance.now();
        animationFrameId = requestAnimationFrame(runAnimation);
      }, delay);
    } else {
      animationFrameId = requestAnimationFrame(runAnimation);
    }

    // Cleanup function
    return () => {
      if (animationFrameId) {
        cancelAnimationFrame(animationFrameId);
      }
      if (delayTimeout) {
        clearTimeout(delayTimeout);
      }
    };
  }, [duration, delay, easing, onComplete]);

  // Reset animation
  const reset = useCallback(() => {
    setProgress(0);
    setIsRunning(false);
  }, []);

  return { progress, isRunning, animate, reset };
}

// Common easing functions
export const easingFunctions = {
  // Linear (no easing)
  linear: (t: number) => t,

  // Quadratic
  easeInQuad: (t: number) => t * t,
  easeOutQuad: (t: number) => t * (2 - t),
  easeInOutQuad: (t: number) => t < 0.5 ? 2 * t * t : -1 + (4 - 2 * t) * t,

  // Cubic
  easeInCubic: (t: number) => t * t * t,
  easeOutCubic: (t: number) => (--t) * t * t + 1,
  easeInOutCubic: (t: number) => t < 0.5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1,

  // Elastic
  easeOutElastic: (t: number) => {
    const p = 0.3;
    return Math.pow(2, -10 * t) * Math.sin((t - p / 4) * (2 * Math.PI) / p) + 1;
  }
};

