import React, { useRef, useState, useEffect, useMemo } from 'react';
import { useDebounce } from './useDebounce';

interface VirtualizedListProps<T> {
  items: T[];
  height: number;
  itemHeight: number;
  renderItem: (item: T, index: number) => React.ReactNode;
  overscan?: number;
  className?: string;
  itemKey?: (item: T, index: number) => string | number;
  onScroll?: (scrollTop: number) => void;
}

/**
 * VirtualizedList component for efficiently rendering large lists
 * Only renders items that are visible in the viewport, plus some overscan
 */
function VirtualizedList<T>({
  items,
  height,
  itemHeight,
  renderItem,
  overscan = 3,
  className = '',
  itemKey = (_, index) => index,
  onScroll
}: VirtualizedListProps<T>) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [scrollTop, setScrollTop] = useState(0);

  // Calculate total height of list
  const totalHeight = items.length * itemHeight;

  // Calculate which items to render based on scroll position
  const { startIndex, endIndex, visibleItems } = useMemo(() => {
    // Calculate the visible range
    const start = Math.max(0, Math.floor(scrollTop / itemHeight) - overscan);
    const end = Math.min(
      items.length - 1,
      Math.ceil((scrollTop + height) / itemHeight) + overscan
    );

    // Create array of visible items with their offsets
    const visibleItemsArray = [];
    for (let i = start; i <= end; i++) {
      visibleItemsArray.push({
        item: items[i],
        index: i,
        offsetTop: i * itemHeight
      });
    }

    return { startIndex: start, endIndex: end, visibleItems: visibleItemsArray };
  }, [items, height, itemHeight, scrollTop, overscan]);

  // Handle scroll events
  const handleScroll = (e: React.UIEvent<HTMLDivElement>) => {
    const newScrollTop = e.currentTarget.scrollTop;
    setScrollTop(newScrollTop);
    onScroll?.(newScrollTop);
  };

  return (
    <div 
      ref={containerRef}
      style={{ height, overflow: 'auto', position: 'relative' }} 
      className={className}
      onScroll={handleScroll}
    >
      {/* Spacer div to maintain scroll area */}
      <div style={{ height: totalHeight, position: 'relative' }}>
        {/* Only render visible items */}
        {visibleItems.map(({ item, index, offsetTop }) => (
          <div 
            key={itemKey(item, index)} 
            style={{ 
              position: 'absolute',
              top: 0,
              transform: `translateY(${offsetTop}px)`,
              width: '100%',
              height: itemHeight
            }}
          >
            {renderItem(item, index)}
          </div>
        ))}
      </div>
    </div>
  );
}

/**
 * Hook for virtualizing a list with dynamic item heights
 * More complex but handles variable height items
 */
export function useVirtualizedList<T>(options: {
  items: T[];
  estimatedItemHeight: number;
  overscan?: number;
  getScrollElement?: () => HTMLElement | null;
}) {
  const { 
    items, 
    estimatedItemHeight, 
    overscan = 3,
    getScrollElement = () => null 
  } = options;

  // Refs for measurements
  const itemHeights = useRef<number[]>([]);
  const [scrollTop, setScrollTop] = useState(0);
  const [containerHeight, setContainerHeight] = useState(0);

  // Measure container height on mount
  useEffect(() => {
    const scrollElement = getScrollElement();
    if (!scrollElement) return;

    setContainerHeight(scrollElement.clientHeight);

    const handleResize = () => {
      setContainerHeight(scrollElement.clientHeight);
    };

    // Handle window resize
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [getScrollElement]);

  // Update scroll position
  const handleScroll = () => {
    const scrollElement = getScrollElement();
    if (!scrollElement) return;

    setScrollTop(scrollElement.scrollTop);
  };

  // Debounce scroll handler for better performance
  const debouncedHandleScroll = useDebounce(handleScroll, 10);

  // Attach scroll listener
  useEffect(() => {
    const scrollElement = getScrollElement();
    if (!scrollElement) return;

    scrollElement.addEventListener('scroll', debouncedHandleScroll);
    return () => scrollElement.removeEventListener('scroll', debouncedHandleScroll);
  }, [getScrollElement, debouncedHandleScroll]);

  // Calculate visible range
  return useMemo(() => {
    // Fill in missing item heights with estimated height
    while (itemHeights.current.length < items.length) {
      itemHeights.current.push(estimatedItemHeight);
    }

    // Calculate cumulative heights for positioning
    let totalHeight = 0;
    const offsetMap = new Map<number, number>();

    for (let i = 0; i < items.length; i++) {
      offsetMap.set(i, totalHeight);
      totalHeight += itemHeights.current[i];
    }

    // Find visible range
    let startIndex = 0;
    let endIndex = items.length - 1;

    // Binary search for start index
    let low = 0;
    let high = items.length - 1;

    while (low <= high) {
      const mid = Math.floor((low + high) / 2);
      const offset = offsetMap.get(mid) || 0;

      if (offset < scrollTop - overscan * estimatedItemHeight) {
        low = mid + 1;
      } else {
        high = mid - 1;
      }
    }

    startIndex = Math.max(0, low - overscan);

    // Find end index
    const visibleBottom = scrollTop + containerHeight;
    let i = startIndex;

    while (i < items.length && (offsetMap.get(i) || 0) < visibleBottom + overscan * estimatedItemHeight) {
      i++;
    }

    endIndex = Math.min(items.length - 1, i);

    // Create array of visible items
    const visibleItems = [];
    for (let i = startIndex; i <= endIndex; i++) {
      visibleItems.push({
        item: items[i],
        index: i,
        offsetTop: offsetMap.get(i) || 0
      });
    }

    return {
      visibleItems,
      totalHeight,
      startIndex,
      endIndex,
      setItemHeight: (index: number, height: number) => {
        itemHeights.current[index] = height;
      }
    };
  }, [items, scrollTop, containerHeight, estimatedItemHeight, overscan]);
}

export default VirtualizedList;

// app/hooks/useThrottle.ts
import { useRef, useEffect, useCallback } from 'react';

/**
 * A custom hook for throttling function calls
 * Implements the throttling technique from the performance checklist
 * 
 * @param callback The function to throttle
 * @param delay The minimum time between function calls in milliseconds
 * @returns A throttled version of the callback
 */
function useThrottle<T extends (...args: any[]) => any>(
  callback: T,
  delay: number = 200
): (...args: Parameters<T>) => void {
  const lastCall = useRef<number>(0);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);
  const lastArgs = useRef<Parameters<T> | null>(null);

  // Clean up any pending timeouts when the component unmounts
  // This prevents memory leaks and unnecessary function calls
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  // Create the throttled function
  return useCallback((...args: Parameters<T>) => {
    const now = Date.now();

    // Store the latest arguments
    lastArgs.current = args;

    // Calculate remaining time until next allowed call
    const remaining = delay - (now - lastCall.current);

    // If we've waited long enough, call the function immediately
    if (remaining <= 0) {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
        timeoutRef.current = null;
      }
      lastCall.current = now;
      callback(...args);
    } 
    // Otherwise, schedule a call with the most recent arguments
    else if (!timeoutRef.current) {
      timeoutRef.current = setTimeout(() => {
        lastCall.current = Date.now();
        timeoutRef.current = null;
        if (lastArgs.current) {
          callback(...lastArgs.current);
        }
      }, remaining);
    }
  }, [callback, delay]);
}

export default useThrottle;

import { useCallback, useEffect, useRef } from 'react';
import { useQuiz } from '../features/quiz/context/QuizContext';
import { QuizProgressService } from '../features/quiz/services/quizProgressService';

/**
 * Hook to automatically save quiz progress periodically and on relevant state changes.
 * Uses a debounce mechanism to prevent too frequent saves.
 * 
 * @param isEnabled - Whether auto-saving is enabled
 * @param debounceMs - Debounce time in milliseconds
 * @param saveOnUnmount - Whether to save when the component unmounts
 * @returns Object with methods to control auto-saving
 */
export const useQuizAutoSave = (
  isEnabled: boolean = true,
  debounceMs: number = 2000,
  saveOnUnmount: boolean = true
) => {
  const { state } = useQuiz();
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const saveCountRef = useRef(0);
  const lastSavedStateRef = useRef({
    currentQuestionIndex: -1,
    userAnswers: {},
  });

  // Clear any existing timer to prevent memory leaks
  const clearTimer = useCallback(() => {
    if (timerRef.current) {
      clearTimeout(timerRef.current);
      timerRef.current = null;
    }
  }, []);

  // Debounced save function
  const debouncedSave = useCallback(() => {
    clearTimer();

    // Check if state has actually changed to avoid unnecessary saves
    if (!isEnabled || 
        (state.currentQuestionIndex === lastSavedStateRef.current.currentQuestionIndex && 
         JSON.stringify(state.userAnswers) === JSON.stringify(lastSavedStateRef.current.userAnswers))) {
      return;
    }

    timerRef.current = setTimeout(async () => {
      try {
        const saved = await QuizProgressService.saveProgress({
          quizId: state.quiz!.id,
          questionTypeFilter: state.quiz!.quiz_type || null,
          currentQuestionIndex: state.currentQuestionIndex,
          userAnswers: state.userAnswers,
          isExplicitlyCompleted: state.isQuizComplete,
        });
        if (saved) {
          saveCountRef.current += 1;
          lastSavedStateRef.current = {
            currentQuestionIndex: state.currentQuestionIndex,
            userAnswers: { ...state.userAnswers },
          };
        }
      } catch (error) {
        console.error("Auto-save error:", error);
      }
    }, debounceMs);
  }, [isEnabled, state.currentQuestionIndex, state.userAnswers, state.quiz, state.isQuizComplete, debounceMs, clearTimer]);

  // Use ref to track previous state to avoid unnecessary saves
  const prevStateRef = useRef({
    currentQuestionIndex: -1,
    userAnswers: {} as any,
  });

  // Trigger save when relevant state changes
  useEffect(() => {
    // Skip if not enabled or quiz isn't loaded yet
    if (!isEnabled || state.isLoading || state.questions.length === 0) {
      return;
    }

    // Skip if state hasn't meaningfully changed
    const currentQuestionIndex = state.currentQuestionIndex;
    const userAnswers = state.userAnswers;

    if (
      prevStateRef.current.currentQuestionIndex === currentQuestionIndex &&
      JSON.stringify(prevStateRef.current.userAnswers) === JSON.stringify(userAnswers)
    ) {
      return;
    }

    // Update prev state ref
    prevStateRef.current = {
      currentQuestionIndex,
      userAnswers: { ...userAnswers },
    };

    // Trigger debounced save
    debouncedSave();
  }, [
    isEnabled,
    state.isLoading,
    state.questions.length,
    state.currentQuestionIndex,
    state.userAnswers,
    debouncedSave,
  ]);

  // Save on unmount if needed
  useEffect(() => {
    return () => {
      clearTimer();
      if (saveOnUnmount && isEnabled && state.quiz) {
        QuizProgressService.saveProgress({
          quizId: state.quiz.id,
          questionTypeFilter: state.quiz.quiz_type || null,
          currentQuestionIndex: state.currentQuestionIndex,
          userAnswers: state.userAnswers,
          isExplicitlyCompleted: state.isQuizComplete,
        }).catch(console.error);
      }
    };
  }, [clearTimer, saveOnUnmount, isEnabled, state]);

  // Force an immediate save
  const forceSave = useCallback(async (): Promise<boolean> => {
    clearTimer();
    try {
      const result = await QuizProgressService.saveProgress({
        quizId: state.quiz!.id,
        questionTypeFilter: state.quiz!.quiz_type || null,
        currentQuestionIndex: state.currentQuestionIndex,
        userAnswers: state.userAnswers,
        isExplicitlyCompleted: state.isQuizComplete,
      });
      if (result) {
        saveCountRef.current += 1;
        lastSavedStateRef.current = {
          currentQuestionIndex: state.currentQuestionIndex,
          userAnswers: { ...state.userAnswers },
        };
      }
      return result;
    } catch (error) {
      console.error("Force save error:", error);
      return false;
    }
  }, [clearTimer, state]);

  return {
    forceSave,
    saveCount: saveCountRef.current,
    disable: () => clearTimer(),
    lastSaved: lastSavedStateRef.current,
  };
};

'use client';

import { useState, useEffect, useRef, useCallback } from 'react';

/**
 * A custom hook for debouncing values
 * Implements the debouncing technique from the performance checklist
 * 
 * @param value The value to debounce
 * @param delay The delay in milliseconds
 * @returns The debounced value
 */
export function useDebounce<T>(value: T, delay: number = 500): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    // Set up the timeout to update the debounced value after the specified delay
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    // Clean up the timeout if the value changes before the delay has elapsed
    // This ensures we always use the most recent value
    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

/**
 * Custom hook for debouncing functions
 * Useful for handling frequent events like window resize, scroll, or input changes
 * 
 * @param fn The function to debounce
 * @param delay The delay in milliseconds
 * @param deps Dependencies array for the function
 * @returns The debounced function
 */
export function useDebouncedCallback<T extends (...args: any[]) => any>(
  fn: T, 
  delay: number = 500,
  deps: any[] = []
): (...args: Parameters<T>) => void {
  // Store the function in a ref to avoid unnecessary re-creation
  const fnRef = useRef<T>(fn);

  // Update the function ref when the function changes
  useEffect(() => {
    fnRef.current = fn;
  }, [fn]);

  // Use ref to store the timeout between renders
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Clear timeout when component unmounts or deps change
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, deps);

  // Return memoized debounced callback
  return useCallback(
    (...args: Parameters<T>) => {
      // Clear any existing timeout
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }

      // Set new timeout
      timeoutRef.current = setTimeout(() => {
        fnRef.current(...args);
      }, delay);
    },
    [delay]
  );
}

/**
 * Custom hook for throttling functions (limit how often a function can be called)
 * Useful for scroll events, mousemove, etc.
 * 
 * @param fn The function to throttle
 * @param limit The minimum time between function calls in milliseconds
 * @returns The throttled function
 */
export function useThrottle<T extends (...args: any[]) => any>(
  fn: T,
  limit: number = 200
): (...args: Parameters<T>) => void {
  const lastRunRef = useRef<number>(0);
  const fnRef = useRef<T>(fn);

  // Update the function ref when the function changes
  useEffect(() => {
    fnRef.current = fn;
  }, [fn]);

  return useCallback(
    (...args: Parameters<T>) => {
      const now = Date.now();

      if (now - lastRunRef.current >= limit) {
        lastRunRef.current = now;
        fnRef.current(...args);
      }
    },
    [limit]
  );
}

// For backward compatibility
export default useDebounce;

// app/hooks/useMemoizedCallback.ts
import React, { useCallback, useRef, useEffect } from 'react';

/**
 * A hook that creates a memoized callback with dependencies tracking
 * Provides deeper memoization than standard useCallback
 * 
 * @param callback The callback function to memoize
 * @param deps Dependencies array for the callback
 * @returns A memoized version of the callback that only changes when dependencies change
 */
function useMemoizedCallback<T extends (...args: any[]) => any>(
  callback: T,
  deps: React.DependencyList
): T {
  // Use refs to track the latest callback and dependencies
  const callbackRef = useRef<T>(callback);
  const depsRef = useRef<React.DependencyList>(deps);

  // Check if dependencies have changed
  let depsChanged = deps.length !== depsRef.current.length;
  if (!depsChanged) {
    for (let i = 0; i < deps.length; i++) {
      if (!Object.is(deps[i], depsRef.current[i])) {
        depsChanged = true;
        break;
      }
    }
  }

  // Update refs if deps changed
  useEffect(() => {
    callbackRef.current = callback;
    depsRef.current = deps;
  });

  // Create a stable callback that always uses the latest function
  return useCallback(
    ((...args: Parameters<T>) => callbackRef.current(...args)) as T,
    // Only change the callback identity if deps have changed
    [depsChanged]
  );
}

export default useMemoizedCallback;

'use client';

import { useState, useEffect, useCallback } from 'react';

// Define breakpoint keys and their corresponding pixel values
export type BreakpointKey = 'mobile' | 'mobile-large' | 'tablet' | 'desktop' | 'desktop-large' | 'desktop-xl';

export type DeviceType = 'mobile' | 'tablet' | 'desktop';

// Extended device type that includes all breakpoints for more granular control
export type ExtendedDeviceType = BreakpointKey;

// Breakpoint definitions (matching Tailwind CSS defaults)
const BREAKPOINTS: Record<BreakpointKey, number> = {
  'mobile': 0,
  'mobile-large': 425,
  'tablet': 768,
  'desktop': 1024,
  'desktop-large': 1440,
  'desktop-xl': 1920,
};

interface ResponsiveClasses {
  mobile?: string;
  'mobile-large'?: string;
  tablet?: string;
  desktop?: string;
  'desktop-large'?: string;
  'desktop-xl'?: string;
}

interface UseResponsiveReturn {
  // Device detection
  isMobile: boolean;
  isTablet: boolean;
  isDesktop: boolean;

  // Device type
  deviceType: DeviceType;

  // Touch device detection
  isTouchDevice: boolean;

  // Screen dimensions
  screenWidth: number;
  screenHeight: number;
  width: number; // Alias for screenWidth
  height: number; // Alias for screenHeight

  // Orientation
  orientation: 'portrait' | 'landscape';

  // Breakpoint utilities
  isBreakpointUp: (breakpoint: BreakpointKey) => boolean;
  isBreakpointDown: (breakpoint: BreakpointKey) => boolean;
  isBreakpointBetween: (min: BreakpointKey, max: BreakpointKey) => boolean;

  // Responsive class utilities
  getResponsiveClasses: (classes: ResponsiveClasses) => string;

  // Current breakpoint
  currentBreakpoint: BreakpointKey;
  breakpoint: BreakpointKey; // Alias for currentBreakpoint
}

// Custom hook for responsive design utilities
export const useResponsive = (): UseResponsiveReturn => {
  const [screenWidth, setScreenWidth] = useState<number>(0);
  const [screenHeight, setScreenHeight] = useState<number>(0);
  const [isTouchDevice, setIsTouchDevice] = useState<boolean>(false);

  // Initialize dimensions and touch detection
  useEffect(() => {
    const updateDimensions = () => {
      setScreenWidth(window.innerWidth);
      setScreenHeight(window.innerHeight);
    };

    const detectTouchDevice = () => {
      setIsTouchDevice(
        'ontouchstart' in window ||
        navigator.maxTouchPoints > 0 ||
        // @ts-ignore
        navigator.msMaxTouchPoints > 0
      );
    };

    // Initial setup
    updateDimensions();
    detectTouchDevice();

    // Add event listeners
    window.addEventListener('resize', updateDimensions);
    window.addEventListener('orientationchange', updateDimensions);

    // Cleanup
    return () => {
      window.removeEventListener('resize', updateDimensions);
      window.removeEventListener('orientationchange', updateDimensions);
    };
  }, []);

  // Device detection based on screen width
  const isMobile = screenWidth < BREAKPOINTS.tablet;
  const isTablet = screenWidth >= BREAKPOINTS.tablet && screenWidth < BREAKPOINTS.desktop;
  const isDesktop = screenWidth >= BREAKPOINTS.desktop;

  // Determine device type
  const deviceType: DeviceType = 
    isMobile ? 'mobile' : 
    isTablet ? 'tablet' : 
    'desktop';

  // Get current breakpoint
  const currentBreakpoint: BreakpointKey = 
    screenWidth >= BREAKPOINTS['desktop-xl'] ? 'desktop-xl' :
    screenWidth >= BREAKPOINTS['desktop-large'] ? 'desktop-large' :
    screenWidth >= BREAKPOINTS.desktop ? 'desktop' :
    screenWidth >= BREAKPOINTS.tablet ? 'tablet' :
    screenWidth >= BREAKPOINTS['mobile-large'] ? 'mobile-large' :
    'mobile';

  // Calculate orientation
  const orientation: 'portrait' | 'landscape' = screenWidth < screenHeight ? 'portrait' : 'landscape';

  // Breakpoint utility functions
  const isBreakpointUp = useCallback((breakpoint: BreakpointKey): boolean => {
    return screenWidth >= BREAKPOINTS[breakpoint];
  }, [screenWidth]);

  const isBreakpointDown = useCallback((breakpoint: BreakpointKey): boolean => {
    return screenWidth < BREAKPOINTS[breakpoint];
  }, [screenWidth]);

  const isBreakpointBetween = useCallback((min: BreakpointKey, max: BreakpointKey): boolean => {
    return screenWidth >= BREAKPOINTS[min] && screenWidth < BREAKPOINTS[max];
  }, [screenWidth]);

  // Get responsive classes based on current breakpoint
  const getResponsiveClasses = useCallback((classes: ResponsiveClasses): string => {
    const activeClasses: string[] = [];

    // Get the current breakpoint class
    const currentClass = classes[currentBreakpoint];
    if (currentClass) {
      activeClasses.push(currentClass);
    }

    // Fallback to lower breakpoints if current is not defined
    if (!currentClass) {
      const breakpointOrder: BreakpointKey[] = [
        'desktop-xl', 'desktop-large', 'desktop', 'tablet', 'mobile-large', 'mobile'
      ];

      for (const bp of breakpointOrder) {
        if (screenWidth >= BREAKPOINTS[bp] && classes[bp]) {
          activeClasses.push(classes[bp]);
          break;
        }
      }
    }

    return activeClasses.join(' ');
  }, [currentBreakpoint, screenWidth]);

  return {
    // Device detection
    isMobile,
    isTablet,
    isDesktop,

    // Device type
    deviceType,

    // Touch device detection
    isTouchDevice,

    // Screen dimensions
    screenWidth,
    screenHeight,
    width: screenWidth, // Alias for screenWidth
    height: screenHeight, // Alias for screenHeight

    // Orientation
    orientation,

    // Breakpoint utilities
    isBreakpointUp,
    isBreakpointDown,
    isBreakpointBetween,

    // Responsive class utilities
    getResponsiveClasses,

    // Current breakpoint
    currentBreakpoint,
    breakpoint: currentBreakpoint, // Alias for currentBreakpoint
  };
};

// Default export for convenience
export default useResponsive;

