// A video tesionial to build trust. 2 or 3 on a wall of love is perfect.
const VideoTestimonial = ({ i }: { i: number }) => {
  const vidRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState<boolean>(false);
  const [isLoading, setIsLoading] = useState<boolean>(false);

  useEffect(() => {
    if (vidRef.current?.readyState != 0) {
      setIsLoading(false);
    }
  }, [vidRef?.current?.readyState]);

  const handlePlayVideo = () => {
    if (isPlaying) {
      vidRef.current.pause();
      setIsPlaying(false);
    } else {
      vidRef.current.play();
      setIsPlaying(true);

      if (vidRef.current?.readyState === 0) setIsLoading(true);
    }
  };

  const testimonial = list[i];

  if (!testimonial) return null;

  return (
    <li
      key={i}
      className="break-inside-avoid max-md:flex justify-center bg-base-100 rounded-lg overflow-hidden flex flex-col"
    >
      <div className="relative w-full">
        {isLoading && (
          <span className="z-40 !h-24 !w-24 !bg-gray-50 absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 loading loading-ring"></span>
        )}
        <video
          className="w-full"
          ref={vidRef}
          poster={testimonial.videoPoster}
          preload="metadata"
          playsInline
          width={testimonial.videoWidth}
          height={testimonial.videoHeight}
          onLoadedData={() => {
            console.log("Video is loaded!");
            setIsLoading(false);
          }}
        >
          <source
            src={testimonial.videoSrc}
            type={testimonial.videoType || "video/mp4"}
          />
          Your browser does not support the videos
        </video>

        {!isPlaying && (
          <div className="absolute bottom-0 -inset-x-4 bg-gray-900/50 blur-lg h-24 translate-y-1/4 animate-opacity"></div>
        )}

        <div className="absolute w-full bottom-0 z-20">
          <div className="flex justify-between items-end p-4">
            <button
              className="group cursor-pointer"
              type="button"
              title="Play video"
              aria-label="Play video"
              onClick={handlePlayVideo}
            >
              {isPlaying ? (
                // PAUSE
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="currentColor"
                  className=" w-14 h-14 fill-gray-50 group-hover:scale-[1.05] duration-100 ease-in drop-shadow-lg animate-opacity"
                >
                  <path
                    fillRule="evenodd"
                    d="M6.75 5.25a.75.75 0 0 1 .75-.75H9a.75.75 0 0 1 .75.75v13.5a.75.75 0 0 1-.75.75H7.5a.75.75 0 0 1-.75-.75V5.25Zm7.5 0A.75.75 0 0 1 15 4.5h1.5a.75.75 0 0 1 .75.75v13.5a.75.75 0 0 1-.75.75H15a.75.75 0 0 1-.75-.75V5.25Z"
                    clipRule="evenodd"
                  />
                </svg>
              ) : (
                // PLAY
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="currentColor"
                  className="w-14 h-14 fill-gray-50 group-hover:scale-[1.05] duration-100 ease-in drop-shadow-lg animate-opacity"
                >
                  <path
                    fillRule="evenodd"
                    d="M4.5 5.653c0-1.427 1.529-2.33 2.779-1.643l11.54 6.347c1.295.712 1.295 2.573 0 3.286L7.28 19.99c-1.25.687-2.779-.217-2.779-1.643V5.653Z"
                    clipRule="evenodd"
                  />
                </svg>
              )}
            </button>

            {!isPlaying && (
              <div className="animate-opacity text-right">
                <p className="text-gray-50 font-medium drop-shadow">
                  {testimonial.name}
                </p>
                <div className="rating">
                  {[...Array(5)].map((_, i) => (
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 20 20"
                      fill="currentColor"
                      className="w-5 h-5 text-accent drop-shadow"
                      key={i}
                    >
                      <path
                        fillRule="evenodd"
                        d="M10.868 2.884c-.321-.772-1.415-.772-1.736 0l-1.83 4.401-4.753.381c-.833.067-1.171 1.107-.536 1.651l3.62 3.102-1.106 4.637c-.194.813.691 1.456 1.405 1.02L10 15.591l4.069 2.485c.713.436 1.598-.207 1.404-1.02l-1.106-4.637 3.62-3.102c.635-.544.297-1.584-.536-1.65l-4.752-.382-1.831-4.401z"
                        clipRule="evenodd"
                      />
                    </svg>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="relative z-20 bg-accent text-accent-content text-base leading-tight font-medium p-4 select-none">
        <p>&quot;{testimonial.text}&quot;</p>
      </div>
    </li>
  );
};

// Enhanced responsive testimonials grid with device-optimized layouts
// Features staggered animations, adaptive grids, and touch-friendly interactions
const Testimonials11 = () => {
  const { isMobile, isTablet, deviceType, isTouchDevice } = useResponsive();

  // Device-specific grid configurations
  const getGridConfig = () => {
    if (isMobile) {
      return {
        columns: 1,
        gap: 'gap-4',
        testimonialGap: 'gap-y-4'
      };
    } else if (isTablet) {
      return {
        columns: 2,
        gap: 'gap-6',
        testimonialGap: 'gap-y-6'
      };
    }
    return {
      columns: 4,
      gap: 'gap-6 sm:gap-8',
      testimonialGap: 'gap-y-6 sm:gap-y-8'
    };
  };

  const gridConfig = getGridConfig();

  return (
    <section className="bg-base-200" id="testimonials">
      <ResponsiveContainer>
        <div className={`
          ${isMobile ? 'py-16 px-4' : isTablet ? 'py-20 px-6' : 'py-24 px-8'}
          max-w-7xl mx-auto
        `}>
          {/* Enhanced header with responsive typography */}
          <AnimatedElement animation="fadeIn" delay={0}>
            <div className={`
              flex flex-col text-center w-full
              ${isMobile ? 'mb-12' : 'mb-20'}
            `}>
              <div className={isMobile ? 'mb-4' : 'mb-8'}>
                <h2 className={`
                  font-extrabold text-base-content
                  ${isMobile ? 'text-2xl sm:text-3xl' : isTablet ? 'text-3xl sm:text-4xl' : 'sm:text-5xl text-4xl'}
                `}>
                  212 makers are already shipping faster!
                </h2>
              </div>
              <p className={`
                mx-auto leading-relaxed text-base-content/80
                ${isMobile ? 'text-sm px-4' : isTablet ? 'text-base lg:w-2/3' : 'lg:w-2/3 text-base'}
              `}>
                Don&apos;t take our word for it. Here&apos;s what they have to say
                about {config.appName}.
              </p>
            </div>
          </AnimatedElement>

          {/* Responsive testimonials grid */}
          <StaggeredContainer delay={200} staggerDelay={100}>
            {isMobile ? (
              // Mobile: Single column layout
              <ul role="list" className={`flex flex-col ${gridConfig.testimonialGap}`}>
                {[...Array(6)].map((e, i) => (
                  <AnimatedElement 
                    key={i} 
                    animation="slideUp" 
                    delay={i * 100}
                  >
                    <li>
                      {i === 3 ? <VideoTestimonial i={i} /> : <Testimonial i={i} />}
                    </li>
                  </AnimatedElement>
                ))}
              </ul>
            ) : (
              // Tablet/Desktop: Multi-column layout
              <ResponsiveGrid
                mobile={1}
                tablet={2}
                desktop={4}
                gap={gridConfig.gap}
                className="max-w-2xl mx-auto lg:max-w-none"
              >
                {/* Column 1 */}
                <li>
                  <ul role="list" className={`flex flex-col ${gridConfig.testimonialGap}`}>
                    {[...Array(3)].map((e, i) => (
                      <AnimatedElement 
                        key={i} 
                        animation="slideUp" 
                        delay={i * 150}
                      >
                        <Testimonial i={i} />
                      </AnimatedElement>
                    ))}
                  </ul>
                </li>

                {/* Column 2-3: Video testimonials (hidden on mobile) */}
                <li className={`
                  ${isTablet ? 'col-span-1' : 'col-span-2'} 
                  grid grid-cols-1 ${isTablet ? '' : 'md:grid-cols-2'} 
                  ${gridConfig.gap}
                `}>
                  <AnimatedElement animation="scale" delay={300}>
                    <VideoTestimonial i={0} />
                  </AnimatedElement>
                  {!isTablet && (
                    <AnimatedElement animation="scale" delay={450}>
                      <VideoTestimonial i={1} />
                    </AnimatedElement>
                  )}
                </li>

                {/* Column 4 */}
                <li>
                  <ul role="list" className={`flex flex-col ${gridConfig.testimonialGap}`}>
                    {[...Array(3)].map((e, i) => (
                      <AnimatedElement 
                        key={i + 6} 
                        animation="slideUp" 
                        delay={(i + 6) * 150}
                      >
                        <Testimonial i={i + 6} />
                      </AnimatedElement>
                    ))}
                  </ul>
                </li>
              </ResponsiveGrid>
            )}
          </StaggeredContainer>

          {/* Debug info for development */}
          {process.env.NODE_ENV === 'development' && (
            <div className="mt-8 pt-4 border-t border-base-300 text-center text-xs text-base-content/60">
              Layout: {gridConfig.columns} columns • Device: {deviceType} • Touch: {isTouchDevice ? 'Yes' : 'No'}
            </div>
          )}
        </div>
      </ResponsiveContainer>
    </section>
  );
};

export default Testimonials11;

